INSERT INTO `p8_admin_log` VALUES ('90','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院申请报废固定资产 残值认定情况说明\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">根据卫健委下达的市财政局《关于印发</span><span style=\"font-size:19px;line-height:200%\">&lt;</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">盐城市市级行政事业单位国有资产处置实施细则</span><span style=\"font-size:19px;line-height:200%\">&gt;</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">的通知》的文件。为进一步加强我院医疗设备的规范化管理，经设备部门的技术鉴定，其中</span><span style=\"font-size:19px;line-height:200%\">2006</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">年购置的核磁共振成像装置一台，原值</span><span style=\"font-size:19px;line-height:200%\">17111000</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">元，由于使用年限已久，设备老旧，图像质量不能满足临床诊断需求，无维修价值，现已停用，符合报废条件；螺旋</span><span style=\"font-size:19px;line-height:200%\">CT</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">购买</span><span style=\"font-size: 19px;line-height:200%\">2000</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">年，原值</span><span style=\"font-size: 19px;line-height:200%\">4862000</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">元，已使用</span><span style=\"font-size:19px;line-height:200%\">18</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">年，停用</span><span style=\"font-size: 19px;line-height:200%\">2</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">年，无维修价值，现两台给予报废，并在财政局“资产云平台”管理系统上进行资产处置申报工作，由院部领导审批，盖章</span><span style=\"font-size:19px;line-height:200%\">,</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">在院内进行公示，对该固定资产给予报废，在设备处联系江苏仁禾中衡咨询公司给资产进行评估议价后确定本次报废资产处置估价</span><span style=\"font-size:19px;line-height:200%\">8</span><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">万元整。</span><span style=\"font-size: 19px;line-height:200%\"> </span></p><p style=\"text-indent: 37px;line-height: 200%\"><span style=\"font-size: 19px;line-height:200%\"></span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%\"> </span></p><p style=\"line-height: 200%; text-align: right;\"><span style=\"font-size:19px;line-height:200%\"> </span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">盐城市第一人民医院</span><span style=\"font-size:19px;line-height:200%\"> </span></p><p style=\"text-align: right;\"><span style=\"font-size:19px;font-family: &#39;Calibri&#39;,&#39;sans-serif&#39;\">2021</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px;font-family:&#39;Calibri&#39;,&#39;sans-serif&#39;\">1</span><span style=\"font-size:19px;font-family:宋体\">月</span><span style=\"font-size:19px;font-family:&#39;Calibri&#39;,&#39;sans-serif&#39;\">31</span><span style=\"font-size: 19px;\"><font face=\"宋体\">日</font></span><span style=\"font-size:19px;font-family:&#39;Calibri&#39;,&#39;sans-serif&#39;\"> </span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-03-15\',\n  \'timestamp\' => \'2021-03-15\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'act