INSERT INTO `p8_admin_log` VALUES ('188','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'市委第二巡察组巡察公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">根据市委</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">统一部署</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">，市委第</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">二</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">巡察组对</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">市第一人民医院</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">党委巡察</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">“回头看”</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正黑体_GBK\">一、巡察时间</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\">202</span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">1</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">年</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">4</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">月</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">12</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">日</span>-</span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">6</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">月</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">11</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">日。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正黑体_GBK\">二、巡察内容</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">以习近平新时代中国特色社会主义思想为指导，</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">紧扣督促做到</span><span style=\"font-family:方正仿宋_GBK\">“两个维护”根本任务，坚守政治巡察职能定位，坚持“发现问题、形成震慑，推动改革、促进发展”的工作方针，重点</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">聚焦贯彻落实党的理论路线方针政策和中央</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">、省市委</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">决策部署情况，聚焦群众身边腐败问题和不正之风，聚焦</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">党组织</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">领导班子和干部队伍建设</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">开展监督。</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">同时，围绕</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">被巡察党组织</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">在高水平全面建成小康社会、</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">推进</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">疫情防控和安全生产工作中承担的职责，有针对性地进行监督。</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">在</span><span style=\"font-family:方正仿宋_GBK\">“回头看”过程中，将重点检查</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">七届市委任期内巡视、巡察、审计等反馈问题的</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">整改主体责任、第一责任人责任履行和</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">整改落实</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">情况。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">根据工作职责，市委巡察组主要受理反映被巡察党组织领导班子及其成员和关键岗位党员、干部问题的来信来电来访，重点是关于违反政治纪律、组织纪律、廉洁纪律、群众纪律、工作纪律、生活纪律等方面的举报和反映。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">其他不属于巡察受理范围的信访问题，将按规定由被巡察</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">对象</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">或有关部门认真处理。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正黑体_GBK\">三、反映渠道</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">市委第</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">二</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">巡察组电话：</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">13160195013</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">（电话受理时间：</span>8:30-18:00<span style=\"font-family:方正仿宋_GBK\">）</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">电子邮箱：</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">ycswxc2@163.com</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">。邮政信箱：</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">江苏省盐城市</span><span style=\"font-family:Times New Roman\">A079</span><span style=\"font-family:方正仿宋_GBK\">号邮政</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">信箱。同时，在</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">新龙广场</span><span style=\"font-family:Times New Roman\">5</span><span style=\"font-family:方正仿宋_GBK\">号楼</span><span style=\"font-family:Times New Roman\">15</span><span style=\"font-family:方正仿宋_GBK\">层</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">设置信访接待处。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">欢迎广大干部群众实事求是反映情况，提出意见和建议，并对市委巡察组工作人员进行监督。</span></span></p><p style=\"text-indent:43px;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">特此公告。</span></span></p><p style=\";line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"></span></p><p style=\";line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"></span></p><p style=\"text-indent:43px;text-align:right;line-height:37px\"><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">中共盐城市委第</span></span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">二</span></span><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\"><span style=\"font-family:方正仿宋_GBK\">巡察组</span> </span></p><p style=\"text-indent:43px;text-align:right;line-height:37px\"><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 21px\">202</span><span style=\"font-family: 方正仿宋_GBK;font-size: 21px\"><span style=\"font-family:Times New Roman\">1</sp