INSERT INTO `p8_admin_log` VALUES ('190','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市文明单位申报公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent:48px\"><span style=\"font-size:24px;font-family:仿宋\">盐城市文明单位是积极开展群众性精神文明创建活动，坚持培育和践行社会主义核心价值观，经济建设、政治建设、文化建设、社会建设、生态文明建设和党的建设全面发展，精神文明建设成绩显著，在全市具有典型示范带动作用的单位。</span></p><p style=\"text-indent:48px\"><span style=\"font-size:24px;font-family:仿宋\">本单位经对照测评标准，严格自查，认为符合条件，主动向有关部门申报参评2019-2020年度盐城市文明单位。根据评选工作要求，请本单位群众和社会各界人士给予监督。如有意见建议，可于自公示日起向市文明办反映。</span></p><p style=\"text-indent:48px\"><span style=\"font-size:24px;font-family:仿宋\">盐城市文明办监督电话：86662036，83068603；电子邮箱：ycshdc2036@163.com。</span></p><p><span style=\"font-size:24px;font-family:仿宋\"></span></p><p style=\"text-align:right\"><span style=\"font-size:24px;font-family:仿宋\"></span></p><p style=\"text-align:right\"><span style=\"font-size: 24px;font-family:仿宋\">盐城市第一人民医院</span></p><p style=\"text-align:right\"><span style=\"font-size:24px;font-family:仿宋\">2021</span><span style=\"font-size:24px;font-family:仿宋\">年3月</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-03-11\',\n  \'timestamp\' => \'2021-03-11\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' 