INSERT INTO `p8_admin_log` VALUES ('246','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'科技促民生 科普惠健康——疾病预防 增进健康\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 30px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召, 5月8日上午，我院认真组织泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者来到盐都康年颐养院，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”护理科普活动。颐养院的60多位老人和30多名护理员积极参与，各项活动有条不紊的开展。 </span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">此次护理专家和志愿者走进养老院，关爱老人，是三级医院护理服务向社区延伸的一个体现，也让医务工作者进一步了解了老人的健康需求和困惑；护理专家通过零距离面对面的交流，不仅为老人提供了专业的健康指导，也进一步提升了护理员的理论水平和实践操作技能，受到了颐养院老人的一致好评。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-09\',\n  \'timestamp\' => \'2019-05-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4cd8c13a5f869110\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1625821473');
