INSERT INTO `p8_admin_log` VALUES ('463','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'862\',\n  \'title\' => \'关于进一步规范关于涉及人的生物医学研究进行伦理审查的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/f7fbb92e372786f7.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_f7fbb92e372786f7.jpg\" border=\"0\"/></a></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5cd80f7e17edc727\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1625824889');
INSERT INTO `p8_admin_log` VALUES ('464','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'862\',\n  \'title\' => \'盐卫科教〔2019〕10号关于组织开展2019年度卫生健康新技术评估工作的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em;\" dir=\"rtl\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">：各县（市、区）卫生健康委，市直属各单位</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为推动我市卫生健康科技进步，促进医学科技成果转化，鼓励引进和开展卫生健康新技术，切实加强新技术规范管理，提高疾病防治和诊疗水平，经研究决定开展2019年度新技术评估工作，现将有关要求通知如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、申报条件</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（一）申报的新技术必须为2014年以后市内首家开展，填补省市同类技术空白，技术指标处于省市领先的医学新技术；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（二）符合《涉及人的生物医学研究伦理审查办法》相关管理规定，得到本单位学术委员会、伦理委员会审核批准。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（三）申报的新技术必须属于成熟、适用、先进的医学新技术，技术机理明确。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（四）申报的新技术与同类技术相比较，其技术思路、原理或方法上有创新和改进，主要性能、技术指标等方面综合优于同类技术；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（五）申报的新技术已成为本单位常规开展的医疗卫生服务项目，并已在本单位实施一年以上；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（六）积累了足以证明本单位已熟练掌握该技术的病例数或使用例次数，并取得一定的社会和经济效益。凡涉及人体者必须由病案管理部门出具具体应用的病案号；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（七）必须具有2016年及以后在国内外公开发行的学术期刊上发表的一篇以上相关技术论文。纯实验或基础性研究论文不作为评审依据。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（八）对于申报《限制临床应用的医疗技术（2015版）》在列医疗技术，应提供省级卫生健康行政部门备案证明。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（九）涉及药物、医疗器械临床试验、体外诊断试剂临床试验、干细胞临床研究必须符合国家药品监督管理局相关规定，并获得国家相关批准、批件。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（十）鼓励医学伦理研究、卫生标准研究、生物样本库研究、已转化并取得良好社会效益的卫生适宜技术申报。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（十一）列入政府或行业技术指南、规范，相关行业学会认可优先推荐。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（十二）不属于卫生健康行政部门废除或者禁止使用技术。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（十三）基础研究类成果或已获得政府、行业科技奖励者不得申报。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（十四）不违反国家有关法律或法规。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、申报要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1、材料要求：申报材料包括《盐城市卫生健康委医学新技术引进奖申报书》、技术总结报告、查新报告、发表的论文、应用例次数证明、产生的社会和经济效益证明（社会和经济效益证明必须由病案和财务部门出具）。申报材料一式一份，申报袋封面须注明申报项目名称、申报单位、申报人。重要资料请使用复印件。所有申报材料，评审结果公布一个月后，作为保密材料销毁。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、审核要求：申报单位及其上级业务主管部门负责对申报材料的真实性、可靠性进行审核，确保申报的新技术引进项目符合国家有关法律和法规要求。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3、数量名额：卫生健康新技术评估实行限额申报。三级医院限额申报15项、其他单位限额申报10项。其中同一科室只能申报2项，个人作为第一申报人只能申报1项。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、其他</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请各申报单位按照EXCEL格式将申报项目名称、完成单位名称、完成人姓名（每人一列）、申报科室、项目所属三级学科代码汇总录入并形成电子版。市直属各单位和县（市、区）卫生健康委统一汇总申报材料，于2019年6月30日前报送市卫生健康委科教处，邮箱ycwjwkjc@163.com，联系电话88335069。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 16px;\"><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\" href=\"http://www.ycsyy.com/upload/files/2019/10/1c275b523b2435d5.doc\" title=\"附件：盐城市卫生健康委医学新技术引进奖申报书.doc\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">附件：盐城市卫生健康委医学新技术引进奖申报书.doc</span></a></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"></span><br/></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　盐城市卫生健康委员会</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　2019年5月23日</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'00e3e738d29c8018\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1625824890');
