INSERT INTO `p8_admin_log` VALUES ('478','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'877\',\n  \'title\' => \'关于2021年端午节假日门诊安排的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align:center\"><strong><span style=\"font-family: 宋体;font-size: 24px\"><span style=\"font-family:宋体\">关于</span>2021<span style=\"font-family:宋体\">年端午节假期门诊安排的通知</span></span></strong></p><p><span style=\";font-family:宋体;font-size:19px\"></span></p><p><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">病员朋友们：</span></span></p><p style=\"text-indent:32px;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">根据国家假期工作安排，结合医院实际情况，我院</span>2021<span style=\"font-family:宋体\">年端午节放假二天半：</span><span style=\"font-family:Times New Roman\">6</span><span style=\"font-family:宋体\">月</span><span style=\"font-family:Times New Roman\">12</span><span style=\"font-family:宋体\">日下午—</span><span style=\"font-family:Times New Roman\">6</span><span style=\"font-family:宋体\">月</span><span style=\"font-family:Times New Roman\">14</span><span style=\"font-family:宋体\">日</span></span><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">。</span></span><span style=\";font-family:宋体;font-size:16px\">6<span style=\"font-family:宋体\">月</span><span style=\"font-family:Times New Roman\">12</span><span style=\"font-family:宋体\">日（周六）上午门诊正常开放</span></span><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">。放假期间，紫薇门诊停诊，其余各院区门诊开展业余服务；节假日期间普通医技检查正常开展；发热门诊、急诊</span>24<span style=\"font-family:宋体\">小时开放。各院区业余门诊具体开放科室安排如下：</span></span></p><p style=\"text-indent:32px;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"></span></p><p style=\"text-indent:32px;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\"><img src=\"http://www.ycsyy.com/upload/images/2021/6/74df3eec753bce30.png\" title=\"image.png\" alt=\"image.png\" width=\"814\" height=\"493\" style=\"width: 814px; height: 493px;\"/><br/><br/>放假期间门诊安排及出诊医师排班信息详询</span>66696879<span style=\"font-family:宋体\">（主院区）、</span></span><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">88508990</span><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">（北院区）、</span>88358506<span style=\"font-family:宋体\">（五官科医院）、</span><span style=\"font-family:Times New Roman\">66698899</span><span style=\"font-family:宋体\">（儿童医院）或登陆</span></span><a href=\"http://www.ycsyy.com/\"><span style=\";font-family:宋体;font-size:16px\">“盐城市第一人民医院”</span></a><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">门户网站</span>→“就医指南”→“门诊排班”→“节假日门诊安排”查询。假期门诊就诊接待请至各院区门诊一站式服务中心，具体安排以当天实际开诊为准。</span></p><p style=\"text-indent:32px;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">感谢您选择在我院就诊！祝您身体健康，节日安康！</span> </span></p><p style=\"text-indent:32px;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"></span></p><p style=\"margin-right:37px\"><span style=\";font-family:宋体;font-size:16px\"></span></p><p style=\"margin-right:37px\"><span style=\";font-family:宋体;font-size:16px\"></span></p><p style=\"margin-right:37px;text-indent:368px\"><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">                         盐城市第一人民医院</span></span></p><p style=\"margin-right:36px;text-align:right;line-height:35px\"><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">二</span>○二一年六月</span><span style=\";font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">九</span></span><span style=\