INSERT INTO `p8_admin_log` VALUES ('792','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'离退休干部处开展消防讲座活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年7月19日下午，市一院离退休干部处邀请市消防队的教官为老同志们作消防知识讲座。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/715a80ce1db45a6b.png\" title=\"image.png\" alt=\"image.png\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/23cf714af7a660b8.png\" title=\"image.png\" alt=\"image.png\" width=\"542\" height=\"344\" style=\"width: 542px; height: 344px;\"/></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-19\',\n  \'timestamp\' => \'2019-07-19\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'65a9289cc43494bb\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055687');
INSERT INTO `p8_admin_log` VALUES ('793','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'党员活动｜讴歌新时代，增添正能量\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 57px; line-height: 3em;\"><span style=\"letter-spacing: 1px; font-size: 20px; font-family: 宋体, SimSun;\">春意盎然，春风送暖。为深入学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，以“讴歌新时代，增添正能量，我看新中国成立70周年”主题教育活动开展为载体，进一步丰富我院离退休党员干部职工的精神文化生活。按照院党委要求，2019年5月9日，市一院老干部支部组织全院140多名离退休党员同志赴淮安红色文化教育和历史文化教育基地，开展党日活动现场教学。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/245709ba8f6c5789.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">周恩来同志是伟大的马克思主义者，伟大的无产阶级革命家、政治家、军事家、外交家，党和国家主要领导人之一，中国人民解放军主要创建人之一，中华人民共和国的开国元勋，是以毛泽东同志为核心的党的第一代中央领导集体的重要成员。他的卓著功勋、崇高品德、光辉人格，深深铭记在全国各族人民心中。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在纪念馆中，老党员同志们学习了周恩来同志的家世家风，了解了他投身革命的光辉历程，倾听了他许多大公无私的感人事迹。在周恩来同志的铜像面前，支部的党员同志们重温入党誓词。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/575b9d7c9318fdcc.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/9398f9c4101a96fd.png\" border=\"0\"/></span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/70510753491b402b.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">不忘初心跟党走，牢记使命勇担当。此次的党日活动，院离退休干部处在医院党委的指导下，精心准备、认真组织，整个活动受到了广大老党员同志们的交口称赞，大家纷纷表示要发挥余热，为医院的基层党建添砖加瓦，为推动医院高质量发展贡献力量。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/967f86e712ee86ad.png\" border=\"0\"/></span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-09\',\n  \'timestamp\' => \'2019-05-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'6c08497fb6df2b23\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055687');
INSERT INTO `p8_admin_log` VALUES ('794','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部推举2018年度正能量之星\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年1月9日下午，市一院老干部支部开展离退休党员2018年度“正能量之星”推荐表彰活动。参会党员积极工作，通过严格的程序推荐出年度的优秀老干部。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/e101d36d2d3264a7.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/12cf09b224ff094f.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/8908b3f2f1a48740.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/bbb919eb0772597a.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-01-09\',\n  \'timestamp\' => \'2019-01-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'472d11703e0adc97\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055688');
INSERT INTO `p8_admin_log` VALUES ('795','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部举办庆祝重阳节比赛活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为丰富我院离退休职工的文化生活，我院离退休干部处在2018年重阳节来临之际，前组织了一场离退休职工掼蛋比赛，选手通过激烈的角逐，产生了优胜奖。图为比赛现场。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/9797b8ddcdc2078d.png\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/6c6ae91d4fde5968.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/1db2770666acb833.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/4452dd7b542507c.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/f40fe610455a5b99.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/2bf997778d447083.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/66ae122a2e3facf1.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 59px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><br/></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年10月11日上午，我院离退休干部处组织部分离退休老同志开展投掷羽毛球比赛，比赛现场热闹纷呈。瞧大家赛的多认真，玩得多开心！</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span><img src=\"http://www.ycsyy.com/upload/images/2019/11/a9be502eb6a348db.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/9010e83b1fdd2a2f.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/86b9df37fa52b4d6.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/33434c6ae09fb7ab.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/9e157d423da2432e.png\" border=\"0\"/></p><p style=\"margin-left: 15px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">获奖了！！！</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/e40f6581bef12fd7.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-09-09\',\n  \'timestamp\' => \'2018-09-09\',\n  \'