INSERT INTO `p8_admin_log` VALUES ('796','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干支部表彰奖励2017年度“正能量之星”\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年1月10日，老干支部表彰了2017年度的“正能量之星”，有多位在支部活动中积极，传播良好风范，家风的老同志受到了表彰奖励。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/5e2b10fb0a40970.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/fb486e7fb009eb92.png\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">表彰奖励2017年度“正能量之星”</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/d6356b06a5e448b9.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/62f1a49fe46260d7.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-01-10\',\n  \'timestamp\' => \'2018-01-10\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'2089bea8871b70f8\',\n  \'views\' => \'[db:浏览