INSERT INTO `p8_admin_log` VALUES ('797','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干支部支部举办“两学一做”知识竞赛活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为深入推进我院离退休党员干部“两学一做”学习教育活动，巩固大家一年来的学习成果，进一步提高我院离退休党员干部的综合素质，在建党96周年到来之际，我院老干部支部开展了离退休党员党建知识竞赛活动。通过抢答、笔试的激烈争夺，徐国旭、冯荣亮等二名同志获一等奖，程树星、郭松林、徐杏棣、李德贵等四名同志获二等奖，候玉珍、周增宽、左明礼、夏玲、陈仁万、周美和等六名同志获三等奖。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/753cefe603573e85.png\" title=\"image.png\" alt=\"image.png\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/8130a05ea43a007f.png\" title=\"image.png\" alt=\"image.png\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/ad82666edae6348c.png\" title=\"image.png\" alt=\"image.png\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/e34cab5c20318cf4.png\" title=\"image.png\" alt=\"image.png\" width=\"314\" height=\"250\" style=\"width: 314px; height: 250px;\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/3d86b5ab666dba2e.png\" title=\"image.png\" alt=\"image.png\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/b1bf1ef05eb9f9ad.png\" title=\"image.png\" alt=\"image.png\" width=\"317\" height=\"245\" style=\"width: 317px; height: 245px;\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/c5e9c2ed23f7730e.png\" title=\"image.png\" alt=\"image.png\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-12-01\',\n  \'timestamp\' => \'2017-12-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7ee3176c4867c74c\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055688');
INSERT INTO `p8_admin_log` VALUES ('798','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部举办“庆国庆、度中秋、迎重阳”系列活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">在2017年国庆、中秋、重阳佳节来临之际， 10月5号市一院离退休职工“庆国庆、度中秋、迎重阳”系列活动拉开了帷幕。老干部踊跃参加，纷纷用行到表达对建国七十周年的热爱。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">王志才、张孝琴夺走了掼蛋比赛的桂冠；李德贵荣获象棋比赛的第一名；杨忠达获书画比赛第一名。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/b0d0aedb3a2c7549.png\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/be4a4bacb881ed00.png\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/70236261e9fce3dd.png\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/13d1ff4e1c4bdaba.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/a27a842be2086f61.png\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/2059ce091703a280.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-10-05\',\n  \'timestamp\' => \'2017-10-05\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'bd488f941ecf55cc\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055689');
INSERT INTO `p8_admin_log` VALUES ('799','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干支部组织离退休老同志参观南区\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2017年9月19日下午，在院领导的关心下，在医院南区办公室的大力支持下，我院离退休干部处组织了部分离退休老同志参观了盐城市第一人民医院南区，让老同志实地了解医院发展的大好形 山册册势，分享医院发展成果，进一步促进老领导、老同志对医院工作的理解与支持。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/2196def87d5e4497.png\" border=\"0\"/></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">老同志们正津津有味地听办公室小罗给大家介绍医院南区的情况</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/34132621d752a147.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/f0b6f9b56b8fe7dc.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/940e2419b9c794d.png\" border=\"0\"/></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">老同志们边走边看，连连称赞我们医院南区真漂亮！医院发展真快！</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/89353f76fbcfd0d5.png\" border=\"0\"/></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">老同志们在门诊药房参观了药房自动发药流程系统，直感叹高科技就是神奇！</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/25a3520cbd92bd23.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/49020444bb939af0.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/7573a115c22d2b38.png\" border=\"0\"/></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">手术室护士长和消化科护士长热情地给大家介绍病区的情况，老同志们问东问西可感兴趣了。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">老同志们兴趣勃勃地追着小罗问这问那，恨不得把自己不清楚的事一下子全弄明白</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/f17bfa6eb8f99c1c.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/ee3b0495f2357b07.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-09-19\',\n  \'timestamp\' => \'2017-09-19\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8e3eff6ad027500e\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055689');
INSERT INTO `p8_admin_log` VALUES ('800','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'我院组织离退休老干部、老同志迎春赏游活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为丰富我院离退休干部职工的精神文化生活，受院党委的委托，我院离退休干部处及老干支部于4月18日和20日两天，分两批组织全院200多名离退休老同志赴南通如皋市水绘园、长寿园参观赏游。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在和煦的春光中，老同志们参观了代表江南园林风格的如皋水绘园，兼具自然秀美和人文底蕴的美景给老同志留下了极其深刻的印象。通过游览，老同志们了解了历史，开阔了眼界，锻炼了身体，颐养了身心。在长寿园，大家浏览了世界最大的寿星像，祈福长寿安康。此外还组织老同志到如皋园艺馆和李昌钰刑侦博物馆赏花、参观，进一步开拓眼界。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">离退休干部处精心准备、悉心负责、考虑周全，整个参观考察活动安全顺畅愉悦，受到了广大老同志的交口称赞。此次活动的成功举办，也标志着老干部支部4月份党员活动日的一项重要内容圆满完成。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/5a7fd8f7f5d329df.jpg\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/3a985cef969b440f.jpg\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/addde6a66634f72e.jpg\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/6991b73812f2d86f.jpg\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/613163a92bec447e.jpg\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/11/3d3d2267df3521e7.jpg\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/cfe56896c410ed64.jpg\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-04-20\',\n  \'timestamp\' => \'2017-04-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'ee533bf003058734\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055690');
INSERT INTO `p8_admin_log` VALUES ('801','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部举办元宵猜灯谜活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为丰富广大离退休干部的文化娱乐生活，让老同志们在欢乐、详和的气氛中迎接元宵佳节，2017年2月9日下午，我院离退休干部科在老干部活动中心组织开展了一次别开生面的猜灯谜活动。图为老同志们在活动现场积极参加猜谜活动。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:21px;font-family: &#39;Times New Roman&#39;,serif\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/c3184c0df3c6fcb4.png\" border=\"0\"/></span></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:21px;font-family: &#39;Times New Roman&#39;,serif\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/2d36d55f3716aeb8.png\" border=\"0\"/></span></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:21px;font-family: &#39;Times New Roman&#39;,serif\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/b7a0de97761bf2c6.png\" border=\"0\"/></span></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:21px;font-family: &#39;Times New Roman&#39;,serif\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/34d41a93e6996868.png\" border=\"0\"/></span></span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-02-09\',\n  \'timestamp\' => \'2017-02-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'0f6ae6bd14064a94\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626055690');
INSERT INTO `p8_admin_log` VALUES ('802','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于举办庆祝中国共产党成立100周年征文比赛的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 32px; text-align: left;\"><span style=\"font-family: 宋体; font-size: 19px;\">各位离退休老同志：</span><br/></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">接省委、市委老干部局、离退休干部工委的通知精神，为庆祝中国共产党成立</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family:宋体\">周年，引导离退休干部讲好党的故事，传承红色基因，弘扬革命精神，特面向我院全体离退休老同志组织开展庆祝中国共产党成立</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family:宋体\">周年征文比赛。现将有关事项通知如下：</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">一、征文主题：围绕庆祝建党</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family: 宋体\">周年，结合亲身经历和所学所思，讲述在党的领导下投身革命、建设、改革的历史瞬间、感人故事和切身体会，讴歌和反映我们党的辉煌成就和宝贵经验，特别是展现新时代在以习近平同志为核心的党中央的坚强领导下，党和国家各项事业取得的历史性成就、发生的历史性变化，我市推进“四新盐城”建设取得的显著成绩，大力弘扬党的光荣传统和优良作风，唱响共产党好、社会主义好、改革开放好、伟大祖国好、各族人民好的主旋律。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">二、征文时间：从通知之日起到</span><span style=\"font-size:19px\">2021</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family: 宋体\">月</span><span style=\"font-size: 19px\">22</span><span style=\"font-size:19px;font-family:宋体\">日为止。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">三、征文体裁：回忆录、散文、纪实文学、学习心得均可，字数一般不超过</span><span style=\"font-size:19px\">3000</span><span style=\"font-size:19px;font-family:宋体\">字。要求内容真实、语言流畅、情感真挚，须是原创作品。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">四、投稿邮箱：</span><span style=\"font-size:19px\">654151792@qq.com</span><span style=\"font-size:19px;font-family:宋体\">，邮件主题命名为“征文比赛</span><span style=\"font-size:19px\">+</span><span style=\"font-size:19px;font-family: 宋体\">作品题目</span><span style=\"font-size: 19px\">+</span><span style=\"font-size:19px;font-family:宋体\">作者姓名”。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">五、奖项设置：一等奖</span><span style=\"font-size:19px\">1</span><span style=\"font-size:19px;font-family: 宋体\">名，二等奖</span><span style=\"font-size:19px\">2</span><span style=\"font-size:19px;font-family: 宋体\">名，三等奖</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family: 宋体\">名，优秀奖若干名，对获奖者将发放一定的奖品。</span></p><p style=\"line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">（本次活动的解释权属于医院离退休干部处）</span></p><p style=\"line-height: 32px; text-align: right;\"><span style=\"font-size:19px\"> </span></p><p style=\"text-indent: 271px; line-height: 32px; text-align: right;\"><span style=\"font-size:19px;font-family:宋体\">盐城市第一人民医院离退休干部处</span></p><p style=\"line-height: 32px; text-align: right;\"><span style=\"font-size:19px\">  2021</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family:宋体\">月</span><span style=\"font-size:19px\">23</span><span style=\"font-size:19px;font-family: 宋体\">日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-04-23\',\n  \'timestamp\' => \'2021-04-23\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'64d99bb8858b8885\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1626055740');
INSERT INTO `p8_admin_log` VALUES ('803','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于离退休职工年度体检的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p><span style=\"font-family: 宋体; font-size: 18px;\"></span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p style=\"text-indent: 0em; text-align: left;\"><span style=\"font-size: 18px;\">各位离退休老同志：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 18px;\">大家好！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 18px;\">经医院统一安排定于2021年4月1日至2021年4月6日（7：00-9：00）为全院离退休职工健康体检。请大家在规定的时间内携带本人身份证直接到体检中心参加体检。本次体检仅限本人，不得转让！体检结束后，务必将体检指引单留交体检中心，否则无法获取体检结果。体检报告在规定体检时间结束一周后，到院老年活动中心领取！请看到通知的老同志相互转告！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体;\"><br/></span></p><p style=\"text-indent:280px\"><span style=\"font-size: 18px;\"></span></p><p style=\"text-indent: 280px; text-align: right;\"><span style=\"font-family: 宋体; font-size: 18px;\">离退休干部处</span></p><p style=\"text-indent: 299px; text-align: right;\"><span style=\"font-size: 18px;\">2021.3.17</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-03-17\',\n  \'timestamp\' => \'2021-03-17\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cm