INSERT INTO `p8_admin_log` VALUES ('804','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'通 知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"></p><p style=\"line-height: 3em; text-indent: 0em;\"></p><p style=\"line-height: 3em; text-indent: 0em; text-align: left;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">：全院离退休职工同志们</span><br/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">正月十五是我国传统的元宵佳节，为丰富广大离退休干部的文化娱乐生活，让老同志们在欢乐、详和的气氛中迎接元宵佳节，离退休干部处将组织猪年“元宵灯谜”活动，欢迎广大离退休职工积极参与。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">活动时间：2019年2月19日下午2：30－3：00</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">活动地点：老干部支部活动室</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市一院离退休干部处</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二O一九年二月十二日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-02-12\',\n  \'timestamp\' => \'2019-02-12\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \