INSERT INTO `p8_admin_log` VALUES ('805','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'盐城市第一人民医院老干部支部纪念改革开放40周年征文启事\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年是我国改革开放40周年。40年来，从城市到乡村，从衣食到住行，从脱贫攻坚到生态文明，中国社会发生了翻天覆地的变化。个人和家庭与国家的变革历程序紧紧相连、息息相关，我们伴随改革开放一路走来，亲历、见证了盐城墙0年来有目共睹、触手可及的巨大变化，感觉最真，情意最切，体会最深。为庆祝改革开放40周年，我院老干部支部组织开展“我看改革开放40年”主题征文活动。现将相关事宜通知如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、征文主题</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">“我看改革开放40年”，改革开放寻与者、奋斗者、见证者的心路与难忘记忆，反映盐城人在40年改革历程中的不懈努力，展示盐城人在新时代的崭新面貌。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、征文对象</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">面向组织关系在老干部支部的所有党员，欢迎大家踊跃投稿。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、征文要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">围绕40年来与改革开放紧密相连的城乡发展、时代变迁、生活和人生轨迹，以亲身经历的故事和身边点点滴滴的变化，以小见大，见微知著，从不同角度、不同侧面，反映改革开放大背景下盐城人民生产生活、风俗习惯、思想观念更新的真实图景，为盐城40年波澜壮阔的发展长卷留下细腻可感、动人心弦的个体“微历史”，鼓舞激励盐城人民沿着改革开放的道路继续前进，凝聚起同心建设“富美盐城”的磅礴力量。征文不限，有真情实感，主题鲜明、条理清晰、生动鲜活，有较强的可读性和感染力。字数控制在3000字以内。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、评选表彰</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次征文设特等奖、一等奖、二等奖、三等奖及优秀奖若干，对获奖者给予不同层次的奖励。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、投稿方式</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">纸质材料邮寄或直接送到老干部支部</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、征文时间</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">即日起至2019年3月1日</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院老干部支部</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年12月9日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-12-09\',\n  \'timestamp\' => \'2018-12-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'76b8f07ebda61ba6\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1626055741');
INSERT INTO `p8_admin_log` VALUES ('806','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于做好市一院离退休干部 “正能量之星”评选推荐工作的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">：各位离退休党员</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(51, 51, 51);\">为推动我院离退休干部党支部建设，2018年来我们离退休干部党支部继续开展了</span><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(34, 34, 34);\">“夕阳生金辉、厚德耀盐城、增添正能量”主题活动，为积极引导全院离退休老干部为党和人民事业增添正能量，为建设新一院作出新的更大贡献</span><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(81, 81, 81);\">，我院老干部支部在2019年新春来临之际，将评选表彰一批“正能量之星”。现将有关事项通知如下： </span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"line-height: 150%; color: rgb(81, 81, 81); font-size: 20px; font-family: 宋体, SimSun;\">一、评选表彰的目的和意义</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(81, 81, 81);\">在离退休党员干部中评选表彰“正能量之星”,</span> <span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(51, 51, 51);\">旨在</span><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(34, 34, 34);\">充分发挥典型的示范引领作用，在离退休干部中营造积极向上的浓厚氛围，为建设新盐城提供强大的精神动力和道德支持，</span><span style=\"font-size: 20px; line-height: 150%; font-family: 宋体; color: rgb(81, 81, 81);\">对我院调动离退休干部党员自我教育、自我提高的积极性和主动性，充分发挥离退休干部党员的先锋模范作用，具有重要意义。</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">二、评选表彰的标准:</span></strong> </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">(</span></strong><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">一)学习之星 </span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（1）注重政治理论学习，在政治思想上行动上与党中央保持一致；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（2）积极参加各类学习活动，并在活动中起带头作用；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（3）喜欢并积极学习新知识，新技能、新技术，不断丰富知识体系；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（4）通过自学或上老年学校，学习并掌握1－2门专业知识或技能；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（5）能运用自己学习成果改变身边老同志对待学习的态度。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（二）健康之星</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（1）拥有健康的生活理念和生活方式，并宣传积极养老观念和健康生活理念；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（2）心理健康。保持积极健康乐观的心态，看待问题客观，面对困难乐观，有良好的社会适应性；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（3）身体健康长寿，日常生活活动正常；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（4）家庭关系和睦，心态乐观向上，人际关系良好；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（5）积极参加健身活动，能够长期坚持1－2项锻炼方法，在老同志健康活动中起带头作用，并能主动自愿带动其他老同志共同锻炼。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（三）关爱之星</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（1）具有一颗关爱他人，助人为乐的慈爱之心；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（2）踊跃加入各级关心下一代工作委员会组织，并积极参加各项活动；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（3）关心青少年的成长工作或结对助学等方面有突出表现；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（4）关爱青少年并积极帮助解决实际困难；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（5）邻里关系和睦，在离退休干部中发挥积极的团结作用。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">(</span></strong><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">四)奉献之星</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（1）大力弘扬“一方有难、八方支援”的革命传统和乐于奉献的社会风尚；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（2）发挥自身优势积极参加志愿者服务活动；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（3）热心社会公益事业，有扶贫帮困、爱心捐赠等方面有突出表现；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（4）带领身边同志积极帮助单位或支部其他需要帮助的老同志；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（五）家风之星</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（1）</span><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">以身作则，言传身教，</span><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">家庭幸福，夫妻互敬，子女孝顺；</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（2）自身身体健康，子女事业有成；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（3）心态乐观向上，以自己的模范行为带动和影响自己子女，亲友和周围的人崇德向善。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); font-size: 20px; font-family: 宋体, SimSun;\">（4）家庭成员遵纪守法，有良好的道德风尚，邻里关系和睦。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（六）参事之星 </span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">（1）</span><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">自觉参加组织生活，在思想上、行动上与党中央和省、市委、医院党委保持高度一致。</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">（2）真心实意为医院改革发展</span><span style=\"font-size: 20px; font-family: 宋体; color: rgb(34, 34, 34);\">建言献策、参与支部管理，在支部管理与建设中发挥积极作用；</span><strong> </strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">（3）</span><span style=\"font-size: 20px; font-family: 宋体; color: rgb(81, 81, 81);\">积极乐观向上<strong>，</strong>保持党员先进本色，为稳定我院离退休干部队伍和谐稳定起着正面的积极作用。</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(81, 81, 81); font-size: 20px; font-family: 宋体, SimSun;\">三、评选推荐的办法和要求:</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; color: rgb(81, 81, 81); font-family: 宋体, SimSun;\">1、评选推荐工作采取党员民主推荐、支委会评议的方式。此次表彰名额为每星2-3人</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; color: rgb(81, 81, 81); font-family: 宋体, SimSun;\">2、凡推荐表彰的“正能量之星”离退休干部党员，均要认真“正能量之星先进事迹”登记表。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(81, 81, 81); font-size: 20px; font-family: 宋体, SimSun;\"> </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"color: rgb(81, 81, 81); font-size: 20px; font-family: 宋体, SimSun;\"></span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"color: rgb(81, 81, 81); font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院老干部支部</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; color: rgb(81, 81, 81); font-family: 宋体, SimSun;\"> 二O一八年十二月九日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-12-09\',\n  \'timestamp\' => \'2018-12-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f90aa73e344ae441\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1626055741');
INSERT INTO `p8_admin_log` VALUES ('807','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于开展纪念建党97周年知识竞赛的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\" dir=\"rtl\"><span style=\"font-family: 宋体; font-size: 20px;\">：我院离退休党员同志</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"text-indent: 2em; font-size: 19px; font-family: 宋体; background: white;\">为深入推进我院离退休党员干部“进一步解放思想，激励新时代新担当新作为”学习教育活动，巩固大家一年来“两学一做”的学习成果</span><span style=\"text-indent: 2em; font-size: 19px; font-family: 宋体;\">，回顾党的历史，增强对党的事业的感情，</span><span style=\"text-indent: 2em; font-size: 19px; font-family: 宋体; background: white;\">进一步提高我院离退休党员干部的综合素质，</span><span style=\"text-indent: 2em; font-size: 19px; font-family: 宋体;\">经院领导同意，在建党97周年到来之际，决定在我院离退休党员中开展纪念建党周年知识竞赛活动。现将有关事项通知如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">一、参赛人员</span></strong></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">党组织关系在老干部支部的党员同志。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">二、竞赛时间</span></strong></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">7月4日下午3：00—4：00。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体; color: black; background: white;\">三、</span><strong><span style=\"font-size: 19px; font-family: 宋体;\">竞赛内容</span></strong></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">以《中国共产党章程》、《中共党史》、习近平《新时代中国特色社会主义思想三十讲》等学习辅导材料内容为主。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">四、参赛方式</span></strong></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">1、笔试题共25题，全部为选择题。集中学习时现场在试题答题。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">五、奖励办法</span></strong></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">获奖奖项根据参赛者答题得分情况由高分到低分采取现场抽奖方式产生。竞赛设一、二、三等奖和纪念奖。其中一等奖2名，二等奖4名，三等奖6名，纪念奖若干。所有获奖人员都将获得相应的奖品。 </span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 20px;\">市一院老干部支部</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 20px;\">二O一八年七月三日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-07-03\',\n  \'timestamp\' => \'2018-07-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'910c2ceae9176d37\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1626055742');
INSERT INTO `p8_admin_log` VALUES ('808','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于组织离退休职工体检的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">：各位离退休老同志</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">你们好！</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在院领导的关心下，在体检中心的各位同仁的大力支持下，我院定于3月19日－3月24日为全院离退休职工安排一次健康检查。每位离退休职工的体检表和体检时间我们都已安排好，请各位老同志本人于3月16日前到院离退休干部处领取（原则上本次体检表不得代领，确有特殊情况需请人代领的同志，请代领人持被领人的身份证），请各位严格按照规定时间去完成体检。组织关系在老干部支部的党员同志来领体检表的时候请一并将2018年度的党费交纳一下！</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">由于离退休职工人员较多，我们不再一一电话通知，凡看到通知的同志，敬请相互转告！谢谢！</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市一院离退休干部处</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年3月13日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-03-13\',\n  \'timestamp\' => \'2018-03-13\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'2f8adbc8199ad24f\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin\',\n)','1626055743');
INSERT INTO `p8_admin_log` VALUES ('809','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'通 知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">：各位离退休老同志，你们好</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">根据国家计生委副主任崔丽同志在全国“两会”期间卫生计生系统安全稳定工作讲话精神要求，院党委充分认识到卫生计生系统安全生产形势的严峻性，立即召开医院安全工作会议，院领导就医院安全、消防安全、后勤保障、信访稳定、卫生应急、应急值守和信息报送等方面工作做了具体的要求。广大离退休老同志虽然离开了工作岗位，但安全与每个人的切身利益密切相关，大家应时刻紧绷安全意识这根弦。请大家自觉做好自身家庭安全隐患的排查工作，保证家里的水、电、气的安全，严防火灾；节日里各种美食诱人，大家要注意食品安全，保护身体健康；走亲访友出行要遵守交通规则，注意阴雨天的交通安全；不扬言滋事或参与大规模的聚集活动，不参与违法越级上访活动，远离非法聚众闹事；不在公共场合或网络上发布反动言论。如发现安全隐患，请及时与医院领导或老干部支部主动联系，以确保两会期间我院离退休干部队伍的稳定。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：13626200808 </span></p><p><br/></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 市一院离退休干部处</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 二O一八年三月一日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-03-01\',\n  \'timestamp\' => \'2018-03-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \