INSERT INTO `p8_admin_log` VALUES ('994','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=898&model=article\',\n  \'cid\' => \'898\',\n  \'title\' => \'张群\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_09/0e5224318236c93a.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，主任医师，本科学历。从事呼吸内科工作20年，对呼吸系统疾病的诊治具有丰富的临床经验，熟练掌握呼吸内科各项常规操作及技能，特别擅长呼吸科急危重症，如慢性阻塞性肺病，支气管哮喘，呼吸衰竭的诊治，熟练运用机械通气，对肺部肿瘤的诊断治疗亦有丰富经验。在省级以上杂志发表论文数篇。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'呼吸危重症及呼吸系统肿瘤诊断和治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-update\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e3b7a75ff4b0053d\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626140654');
INSERT INTO `p8_admin_log` VALUES ('995','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=898&model=article\',\n  \'cid\' => \'898\',\n  \'title\' => \'周志祥\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_09/fed72e378325da33.jpg.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，主任医师，硕士研究生导师。1978年毕业于南通医学院，长期从事临床诊疗工作，熟练掌握呼吸内科基础理论知识，临床各种疾病的诊治和手术操作，对各种呼吸系统疑难危重病人的诊治较有心得。在核心期刊发表论文20余篇；先后获得省科技进步三等奖1项、市科技进步一等奖2项、二等奖3项、市科技新技术新项目引进一等奖2项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'呼吸衰竭、支气管哮喘、支气管肺癌、肺结核病、支气管扩张、肺部感染、慢性阻塞性肺疾病、间质性肺疾病的诊疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e487033660e638e4\',\n  \'verifier\' => \'admin\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626140701');
