INSERT INTO `p8_admin_log` VALUES ('999','1','admin','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=898&model=article\',\n  \'cid\' => \'898\',\n  \'title\' => \'柏宏坚\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_09/d6530443dbf663a5.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'\r\n男，市一院副院长，主任医师，硕士研究生学历，南通大学硕士研究生导师。现任市呼吸病学会名誉主委、省医疗技术管理委员。长期从事呼吸病学的临床、教学及科研工作，对呼吸系统的疑难和危重病的诊治具有丰富的临床经验和娴熟的技能，承担或参与卫生部、省市级科研课题多项。发表学术论文和专著20余篇，其中《中华结核和呼吸杂志》及SCI收录5篇。作为第一完成人和主要参与者的临床研究课题获江苏省科技进步奖、中华医学科技奖、上海市科技进步奖和盐城市政府科技进步奖的奖项。指导数名硕士研究生毕业。首批入选江苏省卫生系统百名医学重点人才计划；先后被评为江苏省有突出贡献的中青年专家、江苏省劳动模范、国家有突出贡献专家并享受国务院政府特殊津贴，为国务院特殊津贴专家。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肺部感染、肺部肿瘤和呼吸重症医学疾病的诊治和预防。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'45d8c6abc510cf50\',\n  \'verifier\' => \'admin\',\n  \'a