INSERT INTO `p8_admin_log` VALUES ('1105','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=page&cid=918&type=4\',\n  \'cid\' => \'918\',\n  \'title\' => \'门诊便民服务措施\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>1、服务协调：及时协调、解决患者诊疗过程中出现的各种问题；对南、北及五官科院区患者的检查、住院及治疗等进行院内协调。</p>\r\n\r\n<p>&nbsp;&nbsp;2、导医导诊：实行门前及分楼层服务；提供路线指引、名医、专科特色等就医信息的介绍；疾病预检分诊；老弱病残和行动不便者的陪检陪送。</p>\r\n\r\n<p>&nbsp;&nbsp;3、优先服务：为急危重症、重度残疾、久病体弱、重度疼痛病人及军人、消防救援人员、离休干部、老年人提供优先诊疗和门诊预约挂号病人优先就诊的审核、证明服务。</p>\r\n\r\n<p>&nbsp;&nbsp;4、审批服务：假日期间疾病诊断证明审批及出院小结盖章。</p>\r\n\r\n<p>&nbsp;&nbsp;5、咨询服务：提供医学咨询、健康咨询服务及检验结果的电话、短信咨询。</p>\r\n\r\n<p>&nbsp;&nbsp;6、预约服务：接受专家、专科挂号的现场及电话预约；门诊特殊病人或病人有特殊要求检查项目的预约服务。</p>\r\n\r\n<p>&nbsp;&nbsp;7、便民服务：为空腹抽血病人提供免费早餐、免费提供开水、纸杯、信纸、信封、针线、雨伞、老花镜、写字板、健康教育处方，失物招领、代寄检验单、提供意见薄和投诉意见箱，提供共享轮椅及平车。</p>\r\n\r\n<p>&nbsp;&nbsp;8、发放报告：自助打印和服务台人工打印、发放各类检验报告及病理报告单。</p>\r\n\r\n<p>&nbsp;&nbsp;9、一站式服务中心电话：</p>\r\n\r\n<p>　　南院区（0515）66696879</p>\r\n\r\n<p>　　北院区（0515）88508990</p>\r\n\r\n<p>　　五官科院区（0515）88358506。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'model\' => \'page\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'27451c988152c2f1\',\n  \'verifier\' => \'admin5\',\n)','1626144004');
