INSERT INTO `p8_admin_log` VALUES ('1109','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=page&cid=919&type=4\',\n  \'cid\' => \'919\',\n  \'title\' => \' 体检中心简介\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>盐城市第一人民医院体检中心中心创建于2006年，位于市中心南城河路12号盐城市第一人民医院五官科医院内，建筑面积约2000多平方米，主要承担市直机关干部和事业单位人员体检、全市专家体检、公务员入职体检、军转干部体检等指令性任务和全市各企事业单位的职工体检、市民个人体检等，获得社会各界一致好评。经过十多年的发展，体检中心具有一定的规模，目前是盐城市业务量最大的体检中心，中心是盐城市医学会健康管理分会、盐城市医院协会健康管理专业委员会主任成员单位，江苏省医学会健康管理分会成员单位，江苏省医院协会疾病与健康管理专业委员会常委单位，同时被中国健康促进会授予&ldquo;大肠癌筛查中心&rdquo;、&ldquo;胃癌筛查中心&rdquo;&ldquo;江苏省体重与肥胖管理中心盐城分中心&rdquo;等称号，2018年接待各类体检8万多人次，业务收入达3500万元。 2017年根据市委市政府要求设立盐城市第一人民医院体检中心中心南区分中心，二个中心实行一体化管理，依托盐城市第一人民医院平台，逐步由单纯的健康体检向健康管理过度，对体检过程中发现的阳性指针进行全程跟踪并提供必要的就医绿色通道；中心科室设置齐全，仪器设备先进，大型医技检查如各种检验、胸部DR、CT、磁共振、胃肠镜检查等共享医院资源；中心现有主任医师、副主任医师、全科医师各一名，承担体检报告的总检并提供必要的健康咨询。每年组织医院专家到各体检单位举办健康讲座和健康咨询十余场次，取得较好的社会效益。</p>\r\n\r\n<p>南院区体检中心：地址：盐城市人民南路66号，电话：0515-66696533&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>五官科医院体检中心：地址：盐城市南城河路12号，电话：0515-88365756</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'model\' => \'page\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'a46ae3789636e03b\',\n  \'verifier\' => \'admin5\',\n)','1626144175');
