INSERT INTO `p8_admin_log` VALUES ('1126','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article&cid=897&type=2\',\n  \'cid\' => \'897\',\n  \'title\' => \'呼吸与危重症医学科简介\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>盐城市第一人民医院呼吸与危急重症医学科成立于1950年，是盐城市首批重点专科和重点学科，2002年被评为江苏省临床重点专科，2007年成为南通大学硕士研究生培养基地， 2014年通过国家药物临床试验资格认证。</p>\r\n\r\n<p>科室现开放床位205张，设有4个独立病区，现有呼吸重症监护病房、肺功能研究室、纤支镜介入中心、睡眠呼吸监测中心、哮喘病研究室。</p>\r\n\r\n<p>现有专业技术人员25人，其中主任医师5人，副主任医师2人；硕士研究生17人，在读博士研究生3人；南通大学副教授、研究生导师2人，教学兼职副教授3人；省&ldquo;333&rdquo;人才2人，省&ldquo;135&rdquo;医学重点人才1人，市&ldquo;111&rdquo;人才2人，市青年医学人才1人；享受国务院特殊津贴1人。</p>\r\n\r\n<p>科室重点开展插管近距离放疗腔内肿瘤、经纤支镜支架置入和气囊扩张治疗气道狭窄、经气管镜纵隔和肺门淋巴结活检术、经气管镜肺活检术、经气管镜电治疗技术、肺泡灌洗、气道炎症无创性评价技术、有创无创机械通气序贯治疗各种类型呼吸衰竭、CPAP治疗睡眠呼吸暂停综合征。</p>\r\n\r\n<p>近年来，科室获得省级科研立项2项、市级科研立项10项、省新技术引进奖二等奖1项、市新技术引进奖一等奖3项、市科技进步奖二等奖3项、市自然科学成果三等奖1项。发表SCI论文10篇，中华级期刊文章3篇，参编著作4部。</p>\r\n\r\n<p><a href=\"http://www.ycsyy.com/upload/images/2019/11/1e0576ae9d74a3a2.jpg\" target=\"_blank\"><img border=\"0\" height=\"328\" src=\"http://www.ycsyy.com/upload/images/2019/11/s_ebd6adc7046ce3f5.png\" width=\"448\" />&nbsp;<img alt=\"\" border=\"0\" height=\"328\" src=\"http://www.ycsyy.com/upload/images/2019/11/s_5385942ce2ec0242.png\" title=\"\" vspace=\"0\" width=\"450\" /><img alt=\"\" border=\"0\" height=\"328\" src=\"http://www.ycsyy.com/upload/images/2019/11/s_df5162823d743a34.png\" title=\"\" vspace=\"0\" width=\"450\" />&nbsp;<img alt=\"\" border=\"0\" height=\"328\" src=\"http://www.ycsyy.com/upload/images/2019/11/s_e1b7ca85f029fe98.png\" title=\"\" vspace=\"0\" width=\"450\" /></a></p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5cdbcb24f0f3aa6c\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626144889');
