INSERT INTO `p8_admin_log` VALUES ('1129','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'932\',\n  \'title\' => \'科技促民生 科普惠健康——先锋社区莲花小区\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">举办地点：先锋社区莲花小区<br/></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">举办时间：2019年05月09日</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目简介：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 组织护理专家下社区，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”活动周系列活动。</span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3f108ce1bb1e806e.jpeg\" width=\"554\" height=\"415\" alt=\"0000497e2e4072323f9dbb885d24ca0\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/48670e61ee0defa.jpeg\" width=\"554\" height=\"415\" alt=\"2f3ea61a9d90256a55386bb00160a9f\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">急诊急救专业学组在莲花社区进行CPR急救操作演示及培训</span><br/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/18dc22f753bfb097.jpeg\" width=\"554\" height=\"415\" alt=\"\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　母婴专业学组在先锋街道莲花路社区现场宣传母乳喂养、孕期保健知识</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/5563d93a3f55ca33.jpeg\" width=\"553\" height=\"415\" alt=\"5d8586cd5ef4ba633c90deb22cd76fd\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　中医科专业学组在先锋街道莲花路社区进行八段锦现场教学</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/d4e2623bf1740be1.jpeg\" width=\"553\" height=\"415\" alt=\"de69e7d431dde9aef56bfaf6aca5b38\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中医科专业学组在先锋街道莲花路社区进行辨证施膳饮食指导</span><br/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b4eda79fa00f238e.jpeg\" width=\"553\" height=\"415\" alt=\"5d9180752faf424aff717e8cdd30dc4\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　消化科专业学组向先锋街道莲花路社区居民进行消化科相关知识的宣传</span><a name=\"_GoBack\"></a></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ab615031fbd4992a.jpeg\" width=\"512\" height=\"415\" alt=\"2\" border=\"0\"/></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/75766bcad06565d7.jpeg\" width=\"554\" height=\"415\" alt=\"6bd924e4306e72e61251af4ead1d7ea\" border=\"0\"/></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-09\',\n  \'timestamp\' => \'2019-05-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f1c58b1703d848ac\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626156906');
INSERT INTO `p8_admin_log` VALUES ('1130','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'932\',\n  \'title\' => \'科技促民生 科普惠健康——盐都康年颐养院\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">举办地点： 盐城盐都康年颐养院</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">举办时间：2019年05月08日</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召,我院护理部认真组织，安排了泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者，在护理部汤琪春主任和孙雯敏副主任的带领下，于5月8日上午8时来到盐都康年颐养院，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”护理科普活动。</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">1、泌尿外科护士长黄建英、心血管内科护士长程赟赟、神经内科护士长丁蓉、普外科护士长刘艳红分别为老人和护理员进行了«前列腺增生的诊疗和护理»、«冠心病的防治»、«脑卒中的识别和预防»、«失禁性皮炎的预防和护理»等专题知识讲座；</span></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7b958a979f50bd89.jpeg\" width=\"553\" height=\"369\" alt=\"85ed00d3257c2af1ee68d4666250e76\" border=\"0\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/408f4e2c32fba36.jpeg\" width=\"553\" height=\"369\" alt=\"c40d01712e4fac569fb9d05696aedc3\" border=\"0\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/64c19ea945338fd3.jpeg\" width=\"554\" height=\"415\" alt=\"fb9e313a7185baee70fc855309f9026\" border=\"0\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"text-indent: 2em; font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/8ecb1827b70fcc80.jpeg\" width=\"554\" height=\"415\" alt=\"a390d186dfe3820c587620ef511f84f\" border=\"0\" style=\"text-indent: 2em;\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f728f20bf48cfb05.jpeg\" width=\"553\" height=\"415\" alt=\"c8ac22a220f8e56a2d501c7a178d11e\" border=\"0\" style=\"text-align: center; text-indent: 2em;\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/17cd90eb0479ceca.jpeg\" width=\"562\" height=\"424\" alt=\"c24bc5367c44ebaf3a7b5dfd2b401b8\" border=\"0\" style=\"text-align: center; text-indent: 2em; width: 562px; height: 424px;\"/></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、神经外科护士长张思敏和骨科陈静老师等为护理员进行了CPR、鼻饲、床上翻身、轮椅搬运法等现场模拟操作训练；</span></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b1ea33641ba2c356.jpeg\" width=\"553\" height=\"369\" alt=\"a154014cb4123dff3e5f16dc0e16ba8\" border=\"0\" style=\"text-indent: 2em;\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/cfbc7327a81f4a03.jpeg\" width=\"553\" height=\"369\" alt=\"3b3e709851585c863b1b8c83a60dead\" border=\"0\" style=\"text-indent: 2em;\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/398cc501a4d52eb0.jpeg\" width=\"553\" height=\"369\" alt=\"1265199127d54029447e6547fd31396\" border=\"0\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f41d4658dfcf9c4d.jpeg\" width=\"553\" height=\"369\" alt=\"4cd16ea84475369982f1b8a15233236\" border=\"0\"/></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">3、护理专家现场为老人免费测血压、血糖等，对心血管、骨科、神经内科、泌尿外科等常见疾病提供咨询、健康指导、发放宣传材料等；</span></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/1130b6bc7bfa81e9.jpeg\" width=\"554\" height=\"362\" alt=\"52e4f233e6611064dfa21ec450598cf\" border=\"0\"/></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/d5a70d5d17136e96.jpeg\" width=\"548\" height=\"730\" alt=\"6dfad6cbe807e4254aa5dca1cef7462\" border=\"0\"/></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-align: left; text-indent: 2em;\">4、汤主任带领泌尿外科和伤口造口护理专业学组的护理专家深入病房，对一例膀胱造瘘术后的病人进行了床旁护理查房，耐心细致的指导了伤口造口护理要点和疾病相关知识健康教育。</span></p><p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/38dc1edbabac80e0.jpeg\" width=\"553\" height=\"369\" alt=\"f2689a9c6aff25ee0472e57c23af087\" border=\"0\" style=\"text-indent: 2em;\"/></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">此次护理专家和志愿者走进养老院，关爱老人，是三级医院护理服务向社区延伸的一个体现，也让我们进一步了解了老人的健康需求和困惑；护理专家通过零距离面对面的交流，不仅为老人提供了专业的健康指导，也进一步提升了护理员的理论水平和实践操作技能，受到了颐养院老人的一致好评。</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-08\',\n  \'timestamp\' => \'2019-05-08\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7cd929384337935d\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626156907');
INSERT INTO `p8_admin_log` VALUES ('1131','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'932\',\n  \'title\' => \'健康中国 科普助力--护理专家咨询、讲座大型公益活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 2017年05月06日组织护理专家下社区，开展了主题为“健康中国 科普助力--护理专家咨询、讲座大型公益活动”。</span><br/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"text-align: center; font-size: 20px; font-family: 宋体, SimSun;\">急诊急救护理学组现</span><span style=\"text-align: center; font-size: 20px; font-family: 宋体, SimSun;\">场演示培训CPR</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span></p><p style=\"line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/93fb4567426af13.jpeg\" width=\"502\" height=\"376\" alt=\"786051953965130641\" border=\"0\"/><br/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">心血管护理学组为居民测量血压、进行护理咨询</span></p><p style=\"text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9af75006c339e86d.jpeg\" width=\"553\" height=\"311\" alt=\"心血管\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　危重症等各护理专业学组专家开展专题讲座</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/41ab319e94d80209.jpeg\" width=\"537\" height=\"332\" alt=\"血糖讲座\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　糖尿病护理学组为居民测量血糖、用食物模型为居民讲解营养知识</span><br/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b7ba07a133c67ab7.png\" width=\"553\" height=\"397\" alt=\"血糖2\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　护理学组现场发放教育处方</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7228e210749a6943.jpeg\" width=\"564\" height=\"350\" alt=\"五官科\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　通过展板开展健康知识宣教</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/edf1fc38efd17edf.jpeg\" width=\"554\" height=\"311\" alt=\"20170506_092850.jpg\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　志愿者为居民送去温暖</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3cf8924faf90db8c.jpeg\" width=\"553\" height=\"311\" alt=\"志愿者\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">现场活动合影留念</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7a178b78a9091929.jpeg\" width=\"553\" height=\"346\" alt=\"合影\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　盐城晚报新闻报道</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/75ee949db40f34e7.png\" width=\"524\" height=\"469\" alt=\"EO%~{W)1[0A{F(2EE1~_}~G\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　盐阜大众报新闻报道</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9d339f4b8fa784d3.png\" width=\"517\" height=\"362\" alt=\"%PQ`)OTK1N$`Z6IRWHZ3F}G\" border=\"0\"/></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　医院官方网站报道</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b0bb54b7e35f390f.png\" width=\"553\" height=\"557\" alt=\"L_0V`VPG1Y0GV]4@XKW%)GG\" border=\"0\"/></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-05-12\',\n  \'timestamp\' => \'2018-05-12\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'bed001ef36353d3d\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626156907');
INSERT INTO `p8_admin_log` VALUES ('1132','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'932\',\n  \'title\' => \'2018“健康中国，科普助力”活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　今年是第107个国际护士节，此次活动是我院根据盐城市护理学会的要求，举办的以“健康中国·科普助力”为主题的大型科普宣传系列活动之一。5月9日盐城市第一人民医院糖尿病、消化、急救、神经科、普外科等护理学组分别到先锋社区、东进社区、光明花苑开展“健康中国，科普助力”活动。活动中，护理专家及医生热情为社区群众答疑解惑，开展多种形式的科普宣传，发放健康处方、测血糖、测血压、评估胰岛素注射部位并宣教，进行糖化血红蛋白筛查，现场急救培训，疾病PPT科普讲座等。活动既送去了医疗服务，增强了社区群众和老人们预防疾病的保健意识，也增进了我院和社区及养老中心的沟通联系，更好地为广大人民群众健康保驾护航。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 护理专科小组在先锋社区合影</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 </span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ad3b5df9a7e42e5c.jpeg\" width=\"518\" height=\"389\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 糖尿病专业学组在先锋社区进行科普讲座</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/549399282f4343f8.jpeg\" width=\"238\" height=\"423\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　内分泌科护士长武建霞在先锋社区进行糖尿病饮食宣教并现场答疑</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/9938244a8ac280a5.jpeg\" width=\"503\" height=\"353\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　医生及专科护士们为社区居民量血压、测血糖、发放健康宣传手册</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/69d11c4963c5abde.jpeg\" width=\"574\" height=\"380\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊急救专业学组为东进社区居民演示CPR急救操作</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/11f993b9b9bfe88b.jpeg\" width=\"323\" height=\"428\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊科宦燕护士长为东进社区居民讲解急救技能知识</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7d8b68337130fe28.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊急救专业学组在东进社区发放急救知识宣传单</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9fd1dbdcbe3ad5.jpeg\" width=\"666\" height=\"500\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　急诊科李春娟护士长在莲花新村进行CPR操作讲解</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b66b1de59d028072.jpeg\" width=\"582\" height=\"436\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">消化科专业学组进入光明花园小区</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/88ffe0314741f51f.jpeg\" width=\"637\" height=\"478\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　消化科专业学组进入光明花园小区为居民进行营养筛查并发放宣传单</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ce11024cb24302bb.jpeg\" width=\"331\" height=\"442\" alt=\"\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/10/a70d79e16ee30858.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　消化科专业学组为光明花苑居民提供健康咨询服务</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3839a82f8e7af505.jpeg\" width=\"364\" height=\"485\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/cba8a7cc47f24aa2.jpeg\" width=\"738\" height=\"554\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　神经科专业学组健康咨询现场</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b59502fc9ee1d54f.jpeg\" width=\"589\" height=\"442\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/45249d0cfec548eb.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　专科护理小组千鹤湾老年医院合影</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/d50071cfdb555599.jpeg\" width=\"580\" height=\"435\" alt=\"\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"text-indent: 2em; font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">王跃琴护士长给千鹤湾养老中心的护理人员、护理员及部分能自理的老年人进行PPT授课</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f97431031d55af35.jpeg\" width=\"467\" height=\"351\" alt=\"o_1cd2gcf2m1qrrtpp1tb28o7e9ga\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/10/663a7f116da6ebe2.jpeg\" width=\"376\" height=\"353\" alt=\"o_1cd2gfrls1a6jk12fl0doq3iia\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">千鹤湾老年医院普外科学组现场咨询、发放宣传资料</span><br/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ac0773f6f65ecd7f.jpeg\" width=\"244\" height=\"326\" alt=\"o_1cd2gprr619lkbn6rg915un183no\" border=\"0\"/><img src=\"http://www.ycsyy.com/upload/images/2019/10/58458e2b7a6f3e2c.jpeg\" width=\"244\" height=\"326\" alt=\"o_1cd2gprqr1jo29nghd617ik1pttg\" border=\"0\"/></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-05-09\',\n  \'timestamp\' => \'2018-05-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_h