INSERT INTO `p8_admin_log` VALUES ('1138','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'933\',\n  \'title\' => \'关于《护士规范化培训》护理专业知识理论结业考试及护理操作技能考核的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">各护理单元及相关科室：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据《护士规范化培训》计划的要求，护理部将对经过规范化培训的相关护理人员进行护理专业知识理论及护理技能操作结业考试、考核工作。具体通知如下：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、对象（具体名单见医院内网）：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 1、2014届护理人员；2、上年度缺考及考核不及格人员。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、考试内容：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　《实用临床护理“三基”——应知应会》</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　《实用临床护理“三基”——个案护理》</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　《护理工作制度手册》</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　《创建资料汇编（第九册）——护理工作流程及应急预案》</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、时间及地点安排</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　理论考试：2017年11月1日15:00—16:00</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　地点：北区急诊七