INSERT INTO `p8_admin_log` VALUES ('1139','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'933\',\n  \'title\' => \'“手术室、供应室一体化管理培训班”的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">各级医疗机构、民营医院：</span></strong></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了进一步加强医院手术室、供应室的质量管理，提升护士专科知识和实践能力，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办《手术室、供应室一体化管理培训班》（项目编号：2017091405011）。现将相关事宜通知如下：</span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体;\">一、培训内容</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">1、<span style=\"font-size: 20px; font-family: 宋体;\">消毒供应中心质量评价体系</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">常州市第一人民医院护理部主任 倪静玉</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">2、<span style=\"font-size: 20px; font-family: 宋体;\">精密贵重器械的高效率运转</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">常州市第一人民医院手术室护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">韩小云</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">3、<span style=\"font-size: 20px; font-family: 宋体;\">消毒供应中心质量风险管理</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">常州市第一人民医院消毒供应中心护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">林素英</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">4<span style=\"font-size: 20px; font-family: 宋体;\">、手术室、供应室护士岗位培训</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">盐城市第一人民医院护理部主任</span>      <span style=\"font-size: 20px; font-family: 宋体;\">汤琪春</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">5<span style=\"font-size: 20px; font-family: 宋体;\">、术中知晓的危害及护理</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院麻醉手术科主任     钱 斌</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">6<span style=\"font-size: 20px; font-family: 宋体;\">、《手术室实践指南（</span>2017版<span style=\"font-size: 20px; font-family: 宋体;\">）》解读</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">盐城市第一人民医院手术室护士长</span>     <span style=\"font-size: 20px; font-family: 宋体;\">陆朝蓉</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\">7<span style=\"font-size: 20px; font-family: 宋体;\">、外来医疗器械的规范化管理</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">盐城市第一人民医院消毒供应中心护士长</span>   <span style=\"font-size: 20px; font-family: 宋体;\">陈</span> <span style=\"font-size: 20px; font-family: 宋体;\">艳</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体;\">二</span></strong><strong><span style=\"font-size: 20px; font-family: 宋体;\">、</span></strong><strong><span style=\"font-size: 20px; font-family: 宋体;\">出席</span></strong><strong><span style=\"font-size: 20px; font-family: 宋体;\">对象</span></strong></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医院护理部主任、护士总长、手术室、供应室护士长及护理骨干。</span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体; font-weight: bold; font-size: 19px;\">三、</span><strong><span style=\"font-size: 20px; font-family: 宋体;\">授发学分</span></strong></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡参会者完成规定的学时，考核合格后授予省级继续教育学分5分。</span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; font-family: 宋体;\">四、时间与地点</span></strong></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">会议时间：</span>2017<span style=\"font-size: 20px; font-family: 宋体;\">年</span>10<span style=\"font-size: 20px; font-family: 宋体;\">月</span>27<span style=\"font-size: 20px; font-family: 宋体;\">日～</span>28<span style=\"font-size: 20px; font-family: 宋体;\">日</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体;\">报到时间：</span>2017<sp