INSERT INTO `p8_admin_log` VALUES ('1141','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'933\',\n  \'title\' => \'“护理管理研讨班”的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医疗机构、民营医院：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为了进一步加强医院护理管理，提高护理管理水平，推进医院护理学科发展，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办《护理管理研讨班》（项目编号：2017091405009）。现将有关培训事宜通知如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、培训内容</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1、专科护理质量监测指标建立与改进</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">东南大学附属中大医院护理部主任  李国宏</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、专科护士核心能力培养与实践</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">江苏省人民医院护理部主任 顾则娟</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3、专业化个案护理实施与案例撰写</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">江苏省人民医院内科科护士长 田金萍</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4、护理工作中法律法规与护理岗位管理</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市护理学会副理事长   戴春兰</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5、护理管理与医院感染管理</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院感染管理科科长兼护理部副主任   谭思源</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6、优质护理服务</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院护理部主任  汤琪春</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7、护理安全管理</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院门急诊重症医学科科护士长 胡 清</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">8、护士临床实践能力培养与考核</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院外科科护士长  薛晓明</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、出席对象</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医院护理管理人员及护理骨干。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、授发学分</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡参会者完成规定的学时，考核合格后授予省级继续教育学分5分。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四、时间与地点</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">会议时间：2017年10月25日～26日</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报到时间：2017年10月25日 07：30～08：30</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报到地点：盐城市第一人民医院门诊七楼会议中心</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">会议地点：盐城市第一人民医院门诊七楼会议中心</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">住宿地点：宏派国际大酒店（江苏省盐城市亭湖区黄海西路135号）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">五、收费标准</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">会务费：每人380元（含培训、材料、证书费）；会务费、差旅费、住宿费回原单位报销。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请参加培训学习人员于2017年10月23日前报名，报名回执统一发至盐城市一院护理部邮箱:ycyyhlb@126.com。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">联系人： 吴开勤 张爱娟 </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">联系电话：0515-83578993 18751438962 18921882600</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　 </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　 盐城市第一人民医院 护理部</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　2017年10月16日</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　抄送：省卫计委、省护理学会、市卫计委、各县（市、区）卫计委</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: left;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　附件1:        护理管理研讨班参会回执</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/763d161e2245c02f.png\" title=\"image.png\" alt=\"image.png\"/></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"></span></p><span style=\"text-indent: 2em;\"></span><p><br/><br/><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-10-16\',\n  \'timestamp\' => \'2017-10-16\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'fad0b45f9b5fbe37\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157012');
INSERT INTO `p8_admin_log` VALUES ('1142','1','admin','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=897\',\n  \'name\' => \'呼吸与危重症医学科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院呼吸与危急重症医学科成立于1950年，是盐城市首批重点专科和重点学科，2002年被评为江苏省临床重点专科，2007年成为南通大学硕士研究生培养基地， 2014年通过国家药物临床试验资格认证。</p>\r\n\r\n<p>科室现开放床位205张，设有4个独立病区，现有呼吸重症监护病房、肺功能研究室、纤支镜介入中心、睡眠呼吸监测中心、哮喘病研究室...</p>\r\n\r\n\',\n    \'linkurl\' => \'http://192.168.162.72/index.php/cms/item-view-id-1954\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobile\' => \'mobile/list\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/category/2021_07/12_20/e2df961ad2e3e616.jpg.thumb.jpg\',\n  \'need_password\' => \'0\',\n  \'category_password\' => \'\',\n  \'domain\' => \'\',\n  \'htmlize\' => \'0\',\n  \'path\' => \'huxiyuweizhongzhengyixueke\',\n  \'html_list_url_rule\' => \'{$core_url}/html/{$id}/#list-{$page}.html#\',\n  \'html_view_url_rule\' => \'{$core_url}/html/{$cid}/{$Y}-{$m}-{$d}/content-{$id}#-{$page}#.html\',\n  \'html_list_url_rule_mobile\' => \'{$core_m_url}/{$id}/#list-{$page}.html#\',\n  \'html_view_url_rule_mobile\' => \'{$core_m_url}/{$cid}/{$Y}-{$m}-{$d}/content-{$id}#-{$page}#.html\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'label_postfix\' => \'\',\n  \'id\' => \'897\',\n)','1626157109');
INSERT INTO `p8_admin_log` VALUES ('1143','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'934\',\n  \'title\' => \'致敬，奋战在疫情防控一线的白衣号长！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">“护士长，这么忙您还有时间对着镜子臭美啊。”门诊部的护士调侃着裴俊芳。她淡淡地一笑，脸上被口罩压出的勒痕格外明显，但依然那么美丽，说话间她整理了一下防护服，又急忙奔赴防疫一线。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">这时电话突然响起，护士赶紧接起电话，无意间在护士长办公桌的抽屉里发现了一张病假条，原来护士长身体不好，去年年底医生就要求她必须休息，这假条一直都藏在抽屉里。疫情来势汹汹，裴俊芳向组织隐瞒了自己的病情，主动请缨，迅速投入到抗击疫情的第一线。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">面对疫情，她逆行而上，面对病人，她温情倍至，完全不顾自己的病情和个人安危。对武汉回来和接触过武汉人的病人前来就诊，她总是主动上前，并全程陪同。裴俊芳还承担着发热门诊繁重的后勤保障和疫情上报工作，保证第一时间与各部门协调解决各类问题。自</span>1<span style=\"font-size: 20px; font-family: 宋体;\">月</span>21<span style=\"font-size: 20px; font-family: 宋体;\">日院党委部署防疫工作以来，她已连续工作</span>9<span style=\"font-size: 20px; font-family: 宋体;\">天，直到部门领导几次三番强制她休息，她仍然坚持每天到班查看。裴护士长还是一位两岁孩子的妈妈，值守在抗击疫情的第一线，下班后她不敢轻易回家，怕影响家人特别是两岁的儿子，春节这个万家团圆的日子，她与孩子虽同在市区，却只能在视频里见面。</span></span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">“虽然我们未上武汉前线，但我们是守在全市人民生命安全和身体健康的第一线！医院需要我们的时候，作为党小组长，一线的护士长，我没有任何理由能躲在其他同事的身后！”护士长和声细语，却落地有声，紧张繁忙的工作和巨大的压力使得裴俊芳原本青春靓丽的脸上添上几许疲惫，但胸前党徽的光芒衬托得她熠熠生辉。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2020-02-04\',\n  \'timestamp\' => \'2020-02-04\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'fa60d850bc2513ea\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626157156');
INSERT INTO `p8_admin_log` VALUES ('1144','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'934\',\n  \'title\' => \'周绚丽——站在抗疫第一线的白衣天使！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">手虽不能相握，心却紧紧相连。这是一场疫情防控的人民战争，从一线医护人员到广大人民群众，许多平凡而伟大的无名英雄奋战在第一线，他们万众一心、凝聚起战胜疫情的人民力量，一个个温情瞬间感人故事，彰显盐城的担当和温度。从即日起，我们栏目推出抗击“疫”情暖心故事专栏，讲述众多正奋战在抗疫第一线普通人的故事。今天我们就一起走近在抗疫第一线的医护人员——市第一人民医院感染性疾病科护士长周绚丽。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">上午</span>9<span style=\"font-size: 20px; font-family: 宋体;\">点，来到市第一人民医院南院的一个特殊病区</span>——<span style=\"font-size: 20px; font-family: 宋体;\">隔离病房病区。整个病区分为三个部分</span>——<span style=\"font-size: 20px; font-family: 宋体;\">清洁区、缓冲区、留观病室。在清洁区的治疗室，我们见到了正在忙碌中的该院感染性疾病科护士长周绚丽。</span></span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院感染性疾病科护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">周绚丽：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">来，我们双人核对，行，</span>20<span style=\"font-size: 20px; font-family: 宋体;\">加</span>30<span style=\"font-size: 20px; font-family: 宋体;\">，</span>50<span style=\"font-size: 20px; font-family: 宋体;\">毫升，我刚才摆了一下，你再看一下，限用日期看一下，</span>2020<span style=\"font-size: 20px; font-family: 宋体;\">年，</span>2020<span style=\"font-size: 20px; font-family: 宋体;\">年</span>9<span style=\"font-size: 20px; font-family: 宋体;\">月，</span>2020<span style=\"font-size: 20px; font-family: 宋体;\">年</span>9<span style=\"font-size: 20px; font-family: 宋体;\">月，好。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">周绚丽说，做好双人核对加药本就是他们护士应尽的义务。而面对留观病室内的重点观察病人，她们工作更是慎之又慎。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">采访中注意到，同一病区，由于污染程度不同，医护人员的防护着装也不尽相同。防护帽、防护镜、手套、口罩以及防护服，成了留观病室内医护人员的标配。一门之隔，对讲机成了最好的沟通工具。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院感染性疾病科护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">周绚丽：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">晓雨、晓雨，（听到了），去看一下</span>34<span style=\"font-size: 20px; font-family: 宋体;\">床。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">周全的防护，密不透风，却也是格外笨重。但即使这样，也从没有人喊声累。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院隔离病房护士金晓雨：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我们现在是每四个小时轮流进来一下，一般是一天</span>24<span style=\"font-size: 20px; font-family: 宋体;\">小时保证不离开人的，早上我一进来，首先是把所有的消毒液，所有的用品都消个毒，擦拭一下，同时也有保洁阿姨进来，把地面房间消个毒，然后把病人的治疗都完成好。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">周绚丽解释道，虽然留观病室处于污染区，但是她们这个临时成立的特殊科室，却从没有人退缩。包括她在内，每一位医护人员随时等待抗击疫情的命令。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院感染性疾病科护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">周绚丽：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我连续几天都早上上班以后，基本上都到晚上</span>10<span style=\"font-size: 20px; font-family: 宋体;\">点左右，还有我们很多护士直接接触病人以后，都很辛苦，为了保证随时服从调遣，她们都是直接睡在医院里面的。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">与此同时，家人的理解与支持，成了周绚丽前进路上最大的动力。谈及丈夫和孩子，她说：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我要成为孩子最好的榜样！</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院感染性疾病科护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">周绚丽：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我老公作为一个医生，他非常支持理解我的工作，我的儿子今年高三，非常懂事，知道妈妈现在走在抗疫的前线，也非常能理解，自己自觉在家里面学习，不要我操心，我说有一个坚强的后盾非常重要。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">家人的理解支持，使得周绚丽以及和她一样坚守岗位的医务人员们毫无顾虑地在护理一线。在距离危险最近的地方，她们相信，她们一定能书写传奇。</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院感染性疾病科护士长</span> <span style=\"font-size: 20px; font-family: 宋体;\">周绚丽：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我们护士服从紧急调遣，也是我们护士的基本职责，所以我们每个人都无怨无悔。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（那你觉得这样工作辛苦吗？会不会感觉辛苦）</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">市第一人民医院隔离病房护士</span> <span style=\"font-size: 20px; font-family: 宋体;\">金晓雨：</span>“<span style=\"font-size: 20px; font-family: 宋体;\">我们这个团队力量还是很强大的，然后大家一起度过难关吧。</span>”</span></p><p style=\"text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">留观病室的病人牵动着每个人的心。作为此次新型冠状病毒感染的肺炎定点收治医院之一，市第一人民医院就是我市抗疫的重要战场，而这间病室便是战疫情的最前沿。在距离危险最近的地方，有着与周绚丽一样的医护人员，为我们遮风避雨、披荆斩棘，让我们向他们致敬！抗击新型冠状病毒感染的肺炎疫情，我们一起共克时艰，并肩战斗，早日打赢这场硬仗！</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2020-02-04\',\n  \'timestamp\' => \'2020-02-04\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e328563980cd308e\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626157156');
INSERT INTO `p8_admin_log` VALUES ('1145','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'934\',\n  \'title\' => \'盐城微笑人物——丁蓉\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 0em; text-align: left;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 28px;\">2008年元月14日晚，“微笑盐城人物”颁奖晚会在盐城电视台演播大厅隆重举行。在晚会现场，一位选手讲述的微笑故事感动了所有的嘉宾和观众，从她的身上，人们感受到微笑和爱的力量，她就是首届“微笑盐城人物”获奖者，盐城市第一人民医院神经内科一病区护士长丁蓉。<br/></span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在为无数患者提供护理服务的过程中，她用白衣天使特有的微笑和爱心演绎着一个又一个感人至极的真情故事。她能根据病人的病情和面部表情变化，细心地为病人作身体和心理护理。每天早上，丁蓉都要到一些重症患者的床前问声好，讲一些鼓励的话语，如果患者看上去心情不好，丁蓉就会给患者讲两句笑话，驱除他们脸上的愁容，帮他们树立起战胜疾病的信心。丁蓉用浓浓的真情呵护着每一位病人，在配合医生做好治疗护理的同时，总是给予病人以亲人般的关爱，一块块奖章、一封封感谢信，充分证明她是一位名副其实的“微笑天使”。</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">丁蓉说，“护理工作者不仅要有过硬的技术，而且更需要将人文关怀贯穿于整个护理工作的过程中，要对患者始终充满爱心、耐心、细心和责任心，要用一颗善良而厚道的心去善待每一位患者”。</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">微笑是爱的使者，微笑是心灵的太阳。丁蓉的微笑，犹如春风化雨，滴滴甘露滋润着患者的心田，她更像天使的翅膀，给患者以温暖、信心和力量</span>……</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体;\">从事护理工作</span><span style=\"font-family: 宋体, SimSun;\">25</span><span style=\"font-family: 宋体;\">年来，她习惯于每天让自己以饱满的精神投入到工作中，让痛苦的患者从她的笑容中看到阳光和美好；她习惯于用心去体察病人和家属的心理，让自己站在患者的角度去理解他们、尊重他们；习惯于用真诚和爱心为患者提供温馨的服务，用精湛的技术赢得患者的信赖。</span><span style=\"font-family: 宋体, SimSun;\"> </span></span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">作为一名护士长，丁蓉同志始终严格自律、以身作责，以实际行动影响着团队中的每位护士。她充分发挥团队精神，大力开展“优质护理服务”工作，全力打造我院“生命所系、健康相托”的服务品牌，受到领导、同事和广大病员的一致好评。多次被医院评为“先进工作者”，被市直卫生系统授予“微笑天使”、“优秀共产党员”、“优秀护士长”和“医德明星”，同时获得市政府颁发的“盐城市新长征突击手”、“微笑盐城人物”奖章，所在病区也多次被评为“先进集体”、“优秀带教病区”并被授予“省级巾帼文明示范岗”和“省级青年文明号”光荣称号。</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">丁蓉同志说“虽然我只是一名普通的护理工作者，但我从对患者的服务中实现了我的人生价值。是组织和广大患者给了我莫大的信任和肯定，我将永远怀着一颗感恩的心，一如既往地做好本职工作，以实际行动继续回报集体、回报社会！”</span></p><p style=\"text-indent: 0em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/65ea7eb864453cb7.JPG\" title=\"1[1].JPG\" alt=\"1[1].JPG\" width=\"442\" height=\"322\" style=\"width: 442px; height: 322px;\"/></span></p><p style=\"text-indent: 0em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/7a0bd94d055756cb.jpg\" title=\"丁蓉病区照片.jpg\" alt=\"丁蓉病区照片.jpg\" width=\"445\" height=\"333\" style=\"width: 445px; height: 333px;\"/></span></p><p style=\"text-indent: 0em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/3db71feb437e5a3c.JPG\" title=\"3[1].JPG\" alt=\"3[1].JPG\" width=\"442\" height=\"338\" style=\"width: 442px; height: 338px;\"/></span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-07\',\n  \'timestamp\' => \'2019-11-07\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1bf79cd9386fc058\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157157');
INSERT INTO `p8_admin_log` VALUES ('1146','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'934\',\n  \'title\' => \'盐城工匠张思敏——让生命之花绚烂开放\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">《青年博览》上有这样一句话：“你不能决定生命的长度，但你可以扩展它的宽度”。而她却改变了这句话——我可能无法扩展你生命的宽度，但我可以尽量延长你生命的长度，她就是盐城工匠张思敏。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">俗话说：“三分靠治，七分靠养。”如果没有护士对病人无微不至的养护，许多病人是无法恢复到现在的状态的。而张思敏护士长就很好的诠释了“白衣天使”这一称号。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、兢兢业业、任劳任怨，用爱感化病患</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">张思敏护士长 2007年参加工作，现任神经外科二病区护士长。她，时刻谨记自己是一名中共党员，多年来，在护理工作岗位上，她勤奋刻苦，兢兢业业，脚踏实地，始终如一，时刻以党员的标准要求自己，始终保持着良好的心理素质和爱岗敬业、任劳任怨的精神，从不计较个人得失，具有很强的团队协作精神。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">在她的带领下，神经外科的护士个个练就了吃苦耐劳、不怕脏不怕累的精神。2016年6月23日，面对突如其来的特大龙卷风冰雹灾害，张思敏和同事们第一时间参与抢救，几天无眠无休，用精湛技术与死神赛跑，给受灾群众带来生的希望。除了正常的工作之外，作为一名科室的管理者，她还要接待省厅专家和来自全国各地的志愿者团队，每天忙碌的工作让她疲惫不堪，但是仍然用微笑面对每一位患者。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、与时俱进、虚心求学，能力提升自我</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">时代在进步，不进则退，业余时间她主动学习新知识、新技能、努力提高自己的理论水平和动手能力，在繁忙的工作之余，她刻苦学习，努力充实自己，参加各种学习班，每次学习归来，她都会把一些先进的护理理念带回来，与全科护士一起实践，带领科室护理同事一起进步，忠实践行“新时代中国社会主义特色思想”。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一分耕耘一分收获，由于工作出色，多次荣获“优秀护士”、“优秀党员”、“先进个人”等称号，</span><a name=\"_GoBack\"></a><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"> 2017年盐城市护理技能竞赛中，张思敏快速、标准、娴熟的护理操作中，赢得现场评委一致认可，以全能组第一名的优异成绩入选盐城工匠的评选。在荣誉面前，她没有沾沾自喜，继续在平凡的岗位上扎实工作，照亮别人，燃烧自己，无私地奉献着。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">张思敏，一个平凡的护士，有一分热发一分光，她以实际行动诠释了白衣天使的神圣内涵;她的敬业，她的仁爱，她用她那娴熟的护理技术让每一朵生命之花开放的更加绚烂。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">她是一位当之无愧的优秀“劳模”!</span></p><p>　　</p><p>　　</p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/2bbeaab4938def41.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_2bbeaab4938def41.jpg\" border=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/c572e652b04179ea.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_c572e652b04179ea.jpg\" border=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/a1fb88caea3a289b.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_a1fb88caea3a289b.jpg\" border=\"0\"/></a></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-21\',\n  \'timestamp\' => \'2019-06-21\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'56f814bfcf5c28ae\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157158');
INSERT INTO `p8_admin_log` VALUES ('1147','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'934\',\n  \'title\' => \'一片丹心天使情——神经外科护士长季丹\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">她以专业的技能、灿烂的微笑驱散着病人的病痛和恐惧，她以女性的温柔、无私的热情温暖着患者的无助和</span><a name=\"_GoBack\"></a><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">冰冷。 她不仅以科学严谨的工作态度在护理领域为患者撑起了一片生命的绿荫，更以高尚的医德医风和令人景仰的大爱深情诠释了一个白衣天使的人格魅力……</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">她，就是市第一人民医院神经外科护士长、副主任护师季丹。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">用责任追求不断超越</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">季丹自 1995 年参加工作、走上护理工作岗位的第一天起，就暗自下定决心要以护理先驱———南丁格尔为榜样，以一丝不苟、严谨求实的态度对待自己所热爱的护理事业。 由于她勤奋好学，工作认真，专业知识、操作技能不断得到巩固和提升，自身素质逐步提高。 2012 年 3 月，她被医院派往北京参加卫生部首届万名护理人才培训。 回来后，她将所学知识运用于实际工作中，率先在全院开展每日护理评估，实施护士分层使用，实行具有专科特点的弹性排班和备班制，减少了交接班频次，更好地应对突发事件的发生。 同时将高年资护士充实到晚夜班，确保了非正常上班时间的护理工作质量，有效地减少了护理差错的发生。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">参加工作 20 多年来， 季丹从一个普通护士成长为一名优秀的基层护理管理者，以自己的实际行动树立了白衣天使的良好形象。 她带领全科护士从细节做起，无论是专科护理还是基础护理，无不体现以患者为中心的服务理念，大大提高了颅脑外伤病人的抢救成功率， 使神经外科的护理水平不断提升，在全市享有很高声誉。由于工作中的出色表现， 季丹连续多年获得多项荣誉：2008 年荣获江苏省“护士岗位技能先进个人”；2009 年荣获“全院十大服务明星”；2010 年荣获江苏省 “优质护理服务标兵”；2011 年荣获江苏省 “优质护理服务先进个人”；2014年获盐城市“最美医护天使”，2015年获盐城市第二届“医德之星”，2018 年荣获盐城市第二届“最美健康卫士”等称号。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">用真情诠释大爱深义</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">季丹处处以身作则，以自己的实际行动影响和带动科室的每一位同志。 早上班、晚下班，这对她来说都是家常便饭，遇到病人有困难需要帮忙，她总是热心地为病人排忧解难。 只有帮病人解决了问题她心里才踏实。 在抢救危重病人的第一线上，她不怕苦、不怕累，总是身先士卒，率先垂范。 她团结科室同志，在工作上严格要求，在生活上关心体贴护士。 不管是护士生病或家中孩子生病，她总是给予关心，主动为其调班，甚至自己顶班替班；发现护士遇到工作不顺心有情绪，她便主动找护士谈心，给予支持帮助。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凭着丰富的专科工作经验，近些年，季丹在“6-23阜宁射阳特大自然灾害”和“3-21响水爆炸事件”发生的第一时间，迅速做好科室的统筹安排，镇定的作战指挥，合理的工作调度，忙而不乱，对每位伤员迅速做好伤情的评估，与一线的姐妹一起奋战，心系灾区，用爱心点亮午夜红烛，用激情托起诺亚方舟，这是医者仁心，医德所载。为了病人，季丹舍小家顾大家，把个人的得失抛在脑后，一心扑在自己热爱的事业上，20 多年来， 她总是主动把休息让给别人，把工作留给自己，记不清她在病房已度过多少个节假日。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">用信念铸就一流团队</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">季丹所在的神经外科病房是一个集急、危、重、手术为一体的特殊病房。 针对科室急诊多、危重病人多的特点，她经常组织科内护士，进行各项急救操作的训练，练就了一支有着较强的应变能力，反应敏捷、处置果断的护理队伍。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">作为护士长，季丹带领全科护士从细节做起，无论是专科护理还是基础护理，无不体现以“患者为中心”的服务理念。2011 年 10 月，神经外科优化服务流程，实行陪检无缝隙服务。所有影像检查均由病区负责登记、预约，提前备好检查用药，及时联系运送车辆，全面告知检查的注意事项及费用情况等。 通过开展陪检服务，使患者的各项检查得到了统筹安排，缩短了等候时间。 让患者在做各项辅助检查中，得到热情的服务，拉近了她们与患者的距离。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为了更好地体现人文关怀，针对科室外伤、肿瘤导致昏迷患者多、住院时间长、恢复慢的特点，季丹从 2012 年初推出“呼唤式护理”。 要求护士在做每一项操作时，都要轻声呼唤，耐心沟通，把他们都当作清醒病人看待，真正做到“用爱唤醒每一位病人”。 所有出院患者及家属都对她们一流的技术非常钦佩，对她们良好的服务表示由衷的感激。 2012 年 5 月，季丹参加江苏省《最美·好护士》的微电影拍摄，在“5·12 护士节”期间进行了全省展播。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">这就是季丹，她不是叱咤风云的巾帼英雄，也没有惊天动地的壮举，然而，她却在自己认定和人民重托的平凡的护理岗位上，以无私的爱心、高尚的医德谱写了一曲曲维护生命的动人乐章。</span></p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/8c73002fa1a6c35f.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_8c73002fa1a6c35f.jpg\" border=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/ecb5c54bb9f40e49.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_ecb5c54bb9f40e49.jpg\" border=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.ycsyy.com/upload/images/2019/10/7494e7fa2cc4f561.jpg\" target=\"_blank\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/s_7494e7fa2cc4f561.jpg\" border=\"0\"/></a></p><p>　　</p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-09\',\n  \'timestamp\' => \'2019-05-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'32fefb49be043cf0\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157158');
INSERT INTO `p8_admin_log` VALUES ('1148','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'935\',\n  \'title\' => \'盐城市第一人民医院开展“幸福天使 拥抱阳光”主题心理辅导活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em;\"><span style=\"font-size: 20px;\">在这阳光灿烂，生机盎然的五月，即将迎来第110个国际护士节。2020年，一场突如其来的新冠肺炎疫情席卷全国。疫情发生以来，全院广大护理同仁始终坚守在发热门诊、预检分诊、隔离病区及医疗救治、疫情防控等抗击疫情的第一线，在人力资源紧张的情况下，不仅要保质保量的完成病区繁忙的护理工作，还要承担大量的疫苗接种和保障任务。盐城市第一人民医院护理人员用自己的微笑和美丽的身影构成了医院无处不在的亮丽风景线；用爱的奉献、爱的力量，让患者重扬生命风帆，重铸身心健康。</span></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/a7449995f085ba7f.png\" style=\"width: 650px; height: 417px;\" title=\"QQ截图20210505100012.png\" width=\"650\" height=\"417\"/></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em; font-size: 20px;\">随着社会不断进步，护理事业飞速发展，对</span><a style=\"text-indent: 2em;\" name=\"_GoBack\"></a><span style=\"text-indent: 2em; font-size: 20px;\">护理工作要求日益增高。护理工作性质特殊，艰辛而不易，医院的发展和稳定离不开护士一直以来的敬业奉献、踏实努力。繁重的工作和不规律作息也给护士带来了不同程度的心理压力。</span><span style=\"font-size: 20px;\"><br/></span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 20px;\">为进一步关心关爱临床护理人员心理健康，帮助广大青年护理人员学会自我疏导，2021年5月4日，恰逢第72个“中国青年节”，在院党委的关心和支持下，工会、团委、妇委会和护理部在主院区四楼会议室联合举办《护士心理健康团体辅导活动》，活动由护理部主任孙雯敏主持，特邀盐城市亭湖区司法局国家二级心理咨询师、亭湖区向阳社工服务社公益组织的负责人唐慧老师，共计100余名护士参加活动。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 20px;\">为让可爱的白衣天使们从繁忙的工作中彻底解脱出来，活动在别开生面的游戏中开始。唐老师以“幸福天使，拥抱阳光”为主题，用生动的语言、鲜活的案例，有趣的互动，深入简出的阐释心理自我调节的重要性，现场还进行了腹式呼吸结合冥想放松法，伴随着轻柔的音乐，引导大家凝心聚气，深入其中。活动最后，为纪念建党100周年，秉承华夏儿女信念，传承红色基因，唐慧老师带领所有人员高举红旗，歌唱“我爱你中国”，进一步掀起了大家的爱国热情，全场激情澎湃！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 20px;\">一个多小时很快过去了，大家都意犹未尽。此次活动贴近临床，参与的护士纷纷表示，大家得到身心放松的同时也增强了自信感，增加了大家彼此的友谊。活动圆满结束，护理部就本次心理辅导进行了随机问卷调查，通过本次调查发现，近8成护士表示从未参加过心理减压活动，均希望多举办参加此类活动，纷纷表示收获颇丰，并在今后的工作生活中更加注重自我心理调节，从容处理工作及生活难题，成就自我最美的人生。</span></p><p style=\"text-indent: 2em; text-align: center;\"><span style=\"font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/3e46c24204a270e4.png\" title=\"QQ截图20210505095924.png\" width=\"739\" height=\"424\" style=\"white-space: normal; width: 739px; height: 424px;\"/></span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 20px;\">一直以来，盐城市一院时刻关注临床护理人员的心理状态，今后将逐渐完善“多贴近，常关怀”的模式，常态化开展调查问卷、心理辅导等形式多样的心灵关爱活动，共同护佑临床护理人员的身心健康。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-05-05\',\n  \'timestamp\' => \'2021-05-05\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4246a034e4206d56\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157258');
INSERT INTO `p8_admin_log` VALUES ('1149','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'935\',\n  \'title\' => \'2019.6.12业务学习\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em;\">　<span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　2019年6月12日下午，护理部组织在北区急诊七楼会议中心进行业务学习培训，本次培训主要内容有四个部分，其中包括心电除颤技术、护理人员常见异常心电图的识别、雾化吸入疗法在呼吸疾病中的应用专家共识、消化内镜手术围手术期的管理，院内</span><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">护士踊跃参与本次学习。</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"text-align: center; font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/58a86b95c9ebd830.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\" style=\"text-align: center;\"/></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-12\',\n  \'timestamp\' => \'2019-06-12\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'9e3395c52c718e62\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626157258');
INSERT INTO `p8_admin_log` VALUES ('1150','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'935\',\n  \'title\' => \'手供消一体化继续教育培训班\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\">　　<span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2018年10月13日上午，为了进一步加强医院手术室、供应室的质量管理，提升护士专科知识和实践能力，由我院主办的省级继续教育项目《手术室、供应室一体化管理培训班》在护理部组织及参会代表的共同支持下在门诊七楼会议中心顺利开展。本次继续教育培训班邀请了东南大学附属中大医院李国宏主任、江苏省中医院芮庆林、南京鼓楼医院王巧桂、东南大学附属中大医院王艳花等多位省内专家进行授课，培训围绕《江苏省三级综合医院评审标准实施细则（2017版）》（护理）解读、临床课题选择与申报关键、手术室信息化管理、硬式内镜集中管理的质量控制、麻醉期间呼吸心跳骤停的分析报告、护理安全管理、常见侵入性诊疗相关感染预防控制、围术期下肢深静脉血栓预防的术中护理等8个主题展开。市内外各级医院护理部主任、护士总长、手术室、消毒供应中心护士长、专科护士及护理骨干在会期间进行了面对面探讨和交流，分享管理经验，提升了护理人员业务能力。</span></p><p>　　</p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/b3bbd9b68f30bcb0.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\"/></p><p style=\"text-align:center\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/5903d20fa376f24c.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/981223bfb0212ef9.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-10-13\',\n  \'timestamp\' => \'2018-10-13\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => 