INSERT INTO `p8_admin_log` VALUES ('1151','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'935\',\n  \'title\' => \'急危重症继续教育培训班\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\">　<span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　为进一步加强护理专业技术发展和人才培养，为从事急、危重症护理的人员提供学习新知识、新技能的平台，提高急、危重症救治能力及专科护理质量，2018年10月13日由我院举办的省级继续教育项目《护理管理暨护理安全管理培训班》在门诊七楼会议中心顺利开展。本次继续教育培训班邀请了东南大学附属中大医院李国宏、江苏省中医院芮庆林、东南大学附属中大医院惠晓芳、泰州市第一人民医院郭素萍等多位省内专家进行授课，培训围绕《江苏省三级综合医院评审标准实施细则（2017版）》（护理）解读、临床课题选择与申报关键、常见急危重症的快速识别与处理、静脉用药安全与医用耗材、护理安全管理、液体复苏、常见侵入性诊疗相关感染预防控制、ICU常用评分工具及临床应用、氧疗指南解读及临床应用等9个主题展开。市内外护理部主任、科护士长、ICU、专科监护病房、急诊科等相关科室护士长、专科护士及护理骨干。在会期间进行了面对面探讨和交流，分享管理经验，提升了护理人员业务能力。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/cef24f45b13f0fe0.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\"/><a name=\"_GoBack\"></a></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b086cc6593af6c8d.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\"/></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2018-10-13\',\n  \'timestamp\' => \'2018-10-13\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n 