INSERT INTO `p8_admin_log` VALUES ('1156','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'935\',\n  \'title\' => \'糖尿病专科学组业务学习\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; text-indent: 2em;\">2017年9月27日下午，糖尿病专科学组在老干部楼十二楼会议室进行业务学习培训，本次培训主要内容有三个部分，其中包括糖尿病病人的自我管理、糖尿病病人延续性护理-分阶段达标管理、低血糖病人的规范管理，各病区糖尿病联络护士、院内糖尿病护理专业学组成员及其他院内护士踊跃参与本次学习。</span></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"text-align: center; text-indent: 2em; font-size: 20px; font-family: 宋体, SimSun;\">　　、</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/d9f1cf128b0dd248.jpeg\" width=\"553\" height=\"415\" alt=\"\" border=\"0\" style=\"text-align: center; text-indent: 2em;\"/></p><p>　　</p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2017-09-27\',\n  \'timestamp\' => \'2017-09-27\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'3f92de3cae7bb229\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 =>