INSERT INTO `p8_admin_log` VALUES ('1354','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=936&model=article\',\n  \'cid\' => \'936\',\n  \'title\' => \'曹京燕\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_16/f8e073c1b1b6c42b.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，主任医师，硕士学位，硕士研究生导师。任盐城市医学会心血管病委员、兼职副教授，为江苏省“333工程”高层次人才、盐城市“111工程”重点医学人才、盐城市“兴卫工程”医学重点人才。主要从事心血管病临床和介入诊治。在国内外核心期刊发表论文10余篇，其中SCI收录1篇；获盐城市政府科技进步奖 4项，盐城市医学新技术引进奖1项，盐城市医学科技发展计划项目3项。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'女，主任医师，硕士学位，硕士研究生导师。任盐城市医学会心血管病委员、兼职副教授，为江苏省&ldquo;333工程&rdquo;高层次人才、盐城市&ldquo;111工程&rdquo;重点医学人才、盐城市&ldquo;兴卫工程&rdquo;医学重点人才。主要从事心血管病临床和介入诊治。在国内外核心期刊发表论文10余篇，其中SCI收录1篇；获盐城市政府科技进步奖 4项，盐城市医学新技术引进奖1项，盐城市医学科技发展计划项目3项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'起搏器植入术、射频消融术及高血压病、冠心病、高脂血症、心力衰竭、各种心律失常、深静脉血栓和心血管神经症的治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'acti