INSERT INTO `p8_admin_log` VALUES ('1506','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article&cid=938&type=2\',\n  \'cid\' => \'938\',\n  \'title\' => \'陈国玉\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/b10decf0949444fb.jpg.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，主任医师，本科学历，学士学位。1990年毕业于南京医科大学，从事儿内科临床工作20余年，熟悉儿科各种疾病的诊治，具有丰富的临床经验。在国家级及省级期刊发表论文数篇。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>女，主任医师，本科学历，学士学位。1990年毕业于南京医科大学，从事儿内科临床工作20余年，熟悉儿科各种疾病的诊治，具有丰富的临床经验。在国家级及省级期刊发表论文数篇。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'小儿各种呼吸系统疾病，特别是反复呼吸道感染，哮喘性疾病的诊治，对小儿各种先天性疾病的诊治有独到见解。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'10239fdd6753e0f3\',\n  \'verifier\' => \'admin789\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626167674');
