INSERT INTO `p8_admin_log` VALUES ('1508','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=938&model=article\',\n  \'cid\' => \'938\',\n  \'title\' => \' 许准\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/f396eb8719464fbb.jpg.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，新生儿科主任，主任医师，本科学历。从事儿科临床工作20余年，具有丰富的临床经验，熟练掌握儿科领域的理论知识，儿科疾病的诊断和操作，擅长新生儿疾病、儿童哮喘的诊治。参与儿童唇腭裂术后病理性语言的研究，获盐城市科技进步二等奖。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，新生儿科主任，主任医师，本科学历。从事儿科临床工作20余年，具有丰富的临床经验，熟练掌握儿科领域的理论知识，儿科疾病的诊断和操作，擅长新生儿疾病、儿童哮喘的诊治。参与儿童唇腭裂术后病理性语言的研究，获盐城市科技进步二等奖。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'新生儿疾病的诊治\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'a117a191d9a29646\',\n  \'verifier\' => \'admin789\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626167743');
INSERT INTO `p8_admin_log` VALUES ('1509','2','admin5','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=897\',\n  \'name\' => \'呼吸与危重症医学科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院呼吸与危急重症医学科成立于1950年，是盐城市首批重点专科和重点学科，2002年被评为江苏省临床重点专科，2007年成为南通大学硕士研究生培养基地， 2014年通过国家药物临床试验资格认证。</p>\r\n\r\n<p>科室现开放床位205张，设有4个独立病区，现有呼吸重症监护病房、肺功能研究室、纤支镜介入中心、睡眠呼吸监测中心、哮喘病研究室...</p>\r\n\r\n\r\n\',\n    \'linkurl\' => \'http://192.168.162.72/index.php/cms/item-view-id-2002\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobile\' => \'mobile/list\',\n  \'frame\' =