INSERT INTO `p8_admin_log` VALUES ('1510','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=938&model=article\',\n  \'cid\' => \'938\',\n  \'title\' => \' 胥飞\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/9bdead5938c29473.jpg.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，儿科副主任，主任医师，本科学历。任江苏省医学会儿科分会委员、小儿神经学组成员、小儿风湿免疫学组成员、江苏省医院协会儿童医院分会委员、江苏省抗癫痫协会常务理事、江苏省夜遗尿协作组委员、盐城市医学会儿科分会副主委、兼职副教授。从事儿科临床30余年，熟练掌握儿科领域的理论知识、临床各种疾病的诊疗和操作，有丰富的临床经验尤其对小儿咳喘、发热、惊厥等擅长。以第一作者在核心期刊发表论文10余篇..\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，儿科副主任，主任医师，本科学历。任江苏省医学会儿科分会委员、小儿神经学组成员、小儿风湿免疫学组成员、江苏省医院协会儿童医院分会委员、江苏省抗癫痫协会常务理事、江苏省夜遗尿协作组委员、盐城市医学会儿科分会副主委、兼职副教授。从事儿科临床30余年，熟练掌握儿科领域的理论知识、临床各种疾病的诊疗和操作，有丰富的临床经验尤其对小儿咳喘、发热、惊厥等擅长。以第一作者在核心期刊发表论文10余篇。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肺炎，哮喘，风湿免疫，脑炎，癫痫等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\'