INSERT INTO `p8_admin_log` VALUES ('1520','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=940&model=article\',\n  \'cid\' => \'940\',\n  \'title\' => \'戴夕超\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/6f684387cae0ed8d.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，医学博士，副主任医师，硕士生导师，IASLC（国际肺癌联盟）成员，CSCO会员，中国抗癌协会会员，意大利那不勒斯国家肿瘤中心访问学者。从事肿瘤科临床工作16年，近五年来第一作者发表文章10余篇，其中SCI文章3篇，中华系列文章2篇。2017年获得扬州市自然科学优秀学术论文三等奖。主持盐城市课题一项，参与国家自然科学基金课题一项。 擅长：恶性肿瘤放疗、化疗、分子靶向治疗、免疫治疗等肿瘤综合治疗。 2015.0...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'女，医学博士，副主任医师，硕士生导师，IASLC（国际肺癌联盟）成员，CSCO会员，中国抗癌协会会员，意大利那不勒斯国家肿瘤中心访问学者。从事肿瘤科临床工作16年，近五年来第一作者发表文章10余篇，其中SCI文章3篇，中华系列文章2篇。2017年获得扬州市自然科学优秀学术论文三等奖。主持盐城市课题一项，参与国家自然科学基金课题一项。 擅长：恶性肿瘤放疗、化疗、分子靶向治疗、免疫治疗等肿瘤综合治疗。 2015.08~2016.08月在复旦大学附属肿瘤医院进修恶性肿瘤的规范化治疗，2018.07~10月在意大利那不勒斯国家肿瘤中心学习恶性肿瘤免疫治疗，对恶性肿瘤放疗、化疗、分子靶向治疗、免疫治疗等肿瘤综合治疗有深刻理解。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'恶性肿瘤放疗、化疗、分子靶向治疗、免疫治疗等肿瘤综合治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http: