INSERT INTO `p8_admin_log` VALUES ('1525','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=940&model=article\',\n  \'cid\' => \'940\',\n  \'title\' => \'李剑萍\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/64345fbafda5db59.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'肿瘤内科副主任医师，医学博士。意大利那不勒斯国家癌症中心访问学者。目前是中国抗癌协会肿瘤标志物专业委员会胃癌标志物协作组青年委员、中国抗癌协会热疗专业委员会青年委员、中国老年医学学会肿瘤康复分会青年委员、江苏省老年学学会中西医结合诊疗专业委员会常委、《中国临床研究》杂志编委。熟悉常见肿瘤的的诊断与治疗，尤其擅长消化系统肿瘤、肺癌、黑色素瘤、软组织肿瘤的诊治，并紧跟国内国际专业进展。.\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'肿瘤内科副主任医师，医学博士。意大利那不勒斯国家癌症中心访问学者。目前是中国抗癌协会肿瘤标志物专业委员会胃癌标志物协作组青年委员、中国抗癌协会热疗专业委员会青年委员、中国老年医学学会肿瘤康复分会青年委员、江苏省老年学学会中西医结合诊疗专业委员会常委、《中国临床研究》杂志编委。熟悉常见肿瘤的的诊断与治疗，尤其擅长消化系统肿瘤、肺癌、黑色素瘤、软组织肿瘤的诊治，并紧跟国内国际专业进展。以第一作者发表SCI文章2篇，在国内核心期刊发表多篇专业学术论文。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'消化系统肿瘤、肺癌、黑色素瘤、软组织肿瘤的诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\'