INSERT INTO `p8_admin_log` VALUES ('1526','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=939&model=article\',\n  \'cid\' => \'939\',\n  \'title\' => \'袁源\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/d0f5a9c53280de7e.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，副主任医师，本科学历。从事妇产科工作近20年，精通本专业的基础理论和专业知识，对本专业疾病的诊断、疑难复杂问题的解决、危重病人的抢救有丰富的临床工作经验，熟练掌握宫颈癌、子宫内膜癌、卵巢癌的根治术。在核心期刊发表论文10余篇\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>女，副主任医师，本科学历。从事妇产科工作近20年，精通本专业的基础理论和专业知识，对本专业疾病的诊断、疑难复杂问题的解决、危重病人的抢救有丰富的临床工作经验，熟练掌握宫颈癌、子宫内膜癌、卵巢癌的根治术。在核心期刊发表论文10余篇。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'各类妇科肿瘤、内分泌疾病的诊治，盆底重建术，运用宫腔镜、腹腔镜对妇科肿瘤、不孕症进行诊治等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'cbdcb1a23e6d8c90\',\n  \'verifier\' => \'admin789\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','16261