INSERT INTO `p8_admin_log` VALUES ('1550','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=939&model=article\',\n  \'cid\' => \'939\',\n  \'title\' => \'凡利俊\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/aaf992d109f48ee9.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，妇科主任、主任医师，本科学历，南通大学兼职副教授。现任江苏省中西医结合妇科专业委员会常务委员，盐城市中西医结合妇科学会主任委员，盐城市医学会妇产科学会副主任委员。常规开展在腹腔镜下子宫肌瘤、卵巢肿瘤及妇科恶性肿瘤的分期手术，压力性尿失禁及子宫脱垂，阴道前后壁膨出的手术，对妇科肿瘤压力性尿失禁、盆底脱垂等疾病的手术治疗具有丰富的临床经验。在《中华妇产科杂志》等省级以上刊物发表论文...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，妇科主任、主任医师，本科学历，南通大学兼职副教授。现任江苏省中西医结合妇科专业委员会常务委员，盐城市中西医结合妇科学会主任委员，盐城市医学会妇产科学会副主任委员。常规开展在腹腔镜下子宫肌瘤、卵巢肿瘤及妇科恶性肿瘤的分期手术，压力性尿失禁及子宫脱垂，阴道前后壁膨出的手术，对妇科肿瘤压力性尿失禁、盆底脱垂等疾病的手术治疗具有丰富的临床经验。在《中华妇产科杂志》等省级以上刊物发表论文10余篇，获市科技成果一等奖1项，二等奖4项，三等奖1项，获市新技术引进一等奖2项。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'妇科肿瘤、微创妇科手术、盆底修复、子宫脱垂、阴道前后壁膨出的盆底重建及压力性尿失禁的诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n