INSERT INTO `p8_admin_log` VALUES ('1557','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=941&model=article\',\n  \'cid\' => \'941\',\n  \'title\' => \'孙鼎明\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/1ae14ca3fdbc1420.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，神经内科副主任医师，神经病学博士。南通大学杏林学院临床实习导师，江苏省卒中学会青年委员、江苏省卒中学会重症脑血管病专业委员会委员、江苏省卒中学会血管认知障碍专业委员会会员、江苏省医学会神经病分会神经遗传病及其相关疾病学组会员、江苏省医学会神经病学分会肌病学组会员、盐城市医学会神经内科分会委员。熟练掌握神经内科各种常见病、疑难病例的诊治，娴熟掌握专科危重病例抢救处理。熟练掌握帕金...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，神经内科副主任医师，神经病学博士。南通大学杏林学院临床实习导师，江苏省卒中学会青年委员、江苏省卒中学会重症脑血管病专业委员会委员、江苏省卒中学会血管认知障碍专业委员会会员、江苏省医学会神经病分会神经遗传病及其相关疾病学组会员、江苏省医学会神经病学分会肌病学组会员、盐城市医学会神经内科分会委员。熟练掌握神经内科各种常见病、疑难病例的诊治，娴熟掌握专科危重病例抢救处理。熟练掌握帕金森氏病的诊治，尤其是帕金森氏病中晚期的病情处理。熟练掌握临床各项操作技能，尤其是颅内血肿、脑室出血的微创清除术，腰大池持续引流，脑脊液置换。目前正在积极开展神经介入治疗技术，可常规开展脑血管病的介入治疗。科研方向主要是动脉粥样硬化性脑血管病的防治，认知障碍的功能影像研究，以及帕金森氏病发病机理及治疗。近年来，SCI收录论文3篇，核心期刊发表论文10多篇，参与多项省市级研究课题。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'帕金森氏病的诊治，颅内血肿、脑室出血的微创清除术，腰大池持续引流，脑脊液置换等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4cabde0bd8864c5c\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626169804');
