INSERT INTO `p8_admin_log` VALUES ('1563','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=941&model=article\',\n  \'cid\' => \'941\',\n  \'title\' => \'陈茂刚\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/13_17/60b48fb4139efc9f.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，江苏省突出贡献中青年专家，神经科博士，主任医师，硕士研究生导师，急诊科副主任，江苏省“333”重点人才。担任中国医师协会神经急诊专业委员会委员、江苏省中西结合脑病专业委员会委员，江苏省卒中学会神经介入专业委员会委员，盐城医学会急诊分会副主任委员兼急诊学组组长。熟练掌握神经内科常见病、多发病、疑难病及危重病的诊断、治疗和抢救工作。对脑血管造影术、颈动脉、椎动脉、锁骨下动脉狭窄的支架..\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，江苏省突出贡献中青年专家，神经科博士，主任医师，硕士研究生导师，急诊科副主任，江苏省&ldquo;333&rdquo;重点人才。担任中国医师协会神经急诊专业委员会委员、江苏省中西结合脑病专业委员会委员，江苏省卒中学会神经介入专业委员会委员，盐城医学会急诊分会副主任委员兼急诊学组组长。熟练掌握神经内科常见病、多发病、疑难病及危重病的诊断、治疗和抢救工作。对脑血管造影术、颈动脉、椎动脉、锁骨下动脉狭窄的支架置入术、梗死急性期血管闭塞的动脉溶栓、机械取栓术有较强的临床实践经验。研究方向：脑梗死、脑出血、蛛网膜下腔出血、头痛、眩晕、癫痫、中枢系统感染、脱髓鞘疾病、帕金森病等的基础与临床研究。在各类杂志发表医学论文50余篇，其中SCI收录9篇。参与国家自然基金课题2项，承担市级课题8项，获市科技进步奖与新技术引进奖７项。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'脑梗死、脑出血、蛛网膜下腔出血、头痛、眩晕的诊断和治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'6632751318e26b72\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626170129');
