INSERT INTO `p8_admin_log` VALUES ('1570','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=943&model=article\',\n  \'cid\' => \'943\',\n  \'title\' => \'杨志军\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_09/b124dc397aa89fb5.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，麻醉手术科主任医师，硕士研究生。徐州医科大学临床专业优秀授课老师，徐州医学院麻醉专业优秀带教老师。能独立处理各种危重、疑难病人的麻醉，擅长重症产科麻醉技术。积极主持开展“支气管阻塞器”的新技术，能够解决胸科复杂支气管肺癌手术的麻醉问题，并取得良好的社会效益和经济效益。近3年，荣获江苏省医学新技术引进二等奖1项，发表学术论文多篇。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，麻醉手术科主任医师，硕士研究生。徐州医科大学临床专业优秀授课老师，徐州医学院麻醉专业优秀带教老师。能独立处理各种危重、疑难病人的麻醉，擅长重症产科麻醉技术。积极主持开展&ldquo;支气管阻塞器&rdquo;的新技术，能够解决胸科复杂支气管肺癌手术的麻醉问题，并取得良好的社会效益和经济效益。近3年，荣获江苏省医学新技术引进二等奖1项，发表学术论文多篇。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4fc029b9402af9d6\',\n  \'verifier\' => \'ad