INSERT INTO `p8_admin_log` VALUES ('1574','3','admin789','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=943&model=article\',\n  \'cid\' => \'943\',\n  \'title\' => \'钱斌\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_09/6519f202b7c6676b.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，麻醉手术科主任、主任医师，本科学士。中国研究型医院学会麻醉学专业委员会委员、江苏省医学会麻醉学会委员、江苏省中西医结合协会麻醉学分会常务委员、江苏省医师协会麻醉学分会委员、江苏省麻醉科医疗质量控制中心成员、江苏省麻醉学分会气道管理学组成员、盐城市医学会麻醉分会主任委员。擅长临床危重患者的麻醉处理，尤其在感染性休克、复杂性颅内肿瘤、嗜铬细胞瘤的麻醉处理上有较高的专业技术水平和丰富...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，麻醉手术科主任、主任医师，本科学士。中国研究型医院学会麻醉学专业委员会委员、江苏省医学会麻醉学会委员、江苏省中西医结合协会麻醉学分会常务委员、江苏省医师协会麻醉学分会委员、江苏省麻醉科医疗质量控制中心成员、江苏省麻醉学分会气道管理学组成员、盐城市医学会麻醉分会主任委员。擅长临床危重患者的麻醉处理，尤其在感染性休克、复杂性颅内肿瘤、嗜铬细胞瘤的麻醉处理上有较高的专业技术水平和丰富的临床经验。近3年致力于困难气道的麻醉团队建设和麻醉中超声技术的临床应用。能够解决本市各级医院及周边县市转诊的疑难麻醉病例，对危重病人有迅速协调组织抢救的能力，能果断正确处理各类重症患者的麻醉。近三年，承担江苏省课题1项、盐城市课题2项，荣获盐城市新技术引进奖一等奖2项、二等奖1项，获得专利9项，在国内外核心期刊发表论文6篇。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http: