INSERT INTO `p8_admin_log` VALUES ('1610','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=945&model=article\',\n  \'cid\' => \'945\',\n  \'title\' => \'郝艳萍\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/93b27cf31ef5ddd9.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，消化内科副主任医师，医学博士。从事消化科临床10余年，熟练掌握消化科领域的理论知识，临床多种疾病的诊疗和操作，发表SCI及核心期刊论文10余篇。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'女，消化内科副主任医师，医学博士。从事消化科临床10余年，熟练掌握消化科领域的理论知识，临床多种疾病的诊疗和操作，发表SCI及核心期刊论文10余篇。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'消化系统疾病诊治，胃肠息肉、消化道早癌的内镜诊治，消化道黏膜下肿瘤超声内镜检查及内镜治疗，重症胰腺炎的诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7abaa8024d929ab9\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626229259');
INSERT INTO `p8_admin_log` VALUES ('1611','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=945&model=article\',\n  \'cid\' => \'945\',\n  \'title\' => \'姜中华\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/197ad8f69416ecf9.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\r\n医学博士，副主任医师，毕业于南京医科大学江苏省消化内镜青年委员，江苏省“科技强卫工程”医学青年人才，盐城市“科教兴卫战略工程”医学青年人才。先后在第二军医大学附属长海医院、江苏省人民医院、苏州大学附属第一医院学习消化临床及消化内镜下的各种治疗，熟练掌握消化科常见病及疑难危重病例的诊治，对内镜下胃肠道息肉、消化道出血、狭窄、逆行胰胆管造影诊治胆胰疾病（内镜下胰胆管取石术，胆道支架置入.\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'医学博士，副主任医师，毕业于南京医科大学江苏省消化内镜青年委员，江苏省&ldquo;科技强卫工程&rdquo;医学青年人才，盐城市&ldquo;科教兴卫战略工程&rdquo;医学青年人才。先后在第二军医大学附属长海医院、江苏省人民医院、苏州大学附属第一医院学习消化临床及消化内镜下的各种治疗，熟练掌握消化科常见病及疑难危重病例的诊治，对内镜下胃肠道息肉、消化道出血、狭窄、逆行胰胆管造影诊治胆胰疾病（内镜下胰胆管取石术，胆道支架置入术）、消化道早癌内镜诊治有着丰富的经验；现主持省、市级课题项目2项，目前在SCI收录期刊以第一作者发表论文5篇，中华系列杂志3篇，国家级核心期<a name=\"_GoBack\"></a>刊4篇，获得江苏省新技术引进奖二等奖1项（第一完成人），盐城市新技术引进奖一等奖2项（第一完成人）。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'超声内镜引导下胆胰占位性病变的细胞学和组织学活检术（EUS-FNA+FNB），超声内镜引导下胰腺癌放射性离子、H101植入术 ，胰腺癌晚期顽固性疼痛腹腔神经节阻滞术（EUS-CPN），胰腺假性囊肿的支架引流术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d63fd9c8c7bf5e6c\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626229335');
INSERT INTO `p8_admin_log` VALUES ('1612','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=945&model=article\',\n  \'cid\' => \'945\',\n  \'title\' => \'王正江\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/107a300cbea2e1dc.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，副主任医师，本科学历。对消化系统常见病、多发病、疑难病诊治，肝、胆、胰疾病的内镜微创介入诊治，内镜逆行胰胆管造影、超声内镜诊治、内镜下黏膜切除、内镜下息肉摘除等，急、慢性胰腺炎、消化道狭窄和各种胆总管结石、阻塞性黄疸的介入和内科治疗有丰富的经验。在省级及中华期刊发表多篇论文；获盐城市卫生局新技术引进一等奖，获盐城市科技进步奖1项。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，副主任医师，本科学历。对消化系统常见病、多发病、疑难病诊治，肝、胆、胰疾病的内镜微创介入诊治，内镜逆行胰胆管造影、超声内镜诊治、内镜下黏膜切除、内镜下息肉摘除等，急、慢性胰腺炎、消化道狭窄和各种胆总管结石、阻塞性黄疸的介入和内科治疗有丰富的经验。在省级及中华期刊发表多篇论文；获盐城市卫生局新技术引进一等奖，获盐城市科技进步奖1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'消化道狭窄、胆总管结石及梗阻性黄疸内镜微创诊疗、重症急性胰腺炎的救治及超声胃镜诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'94bcc3463945cfb2\',\n  