INSERT INTO `p8_admin_log` VALUES ('1617','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=945&model=article\',\n  \'cid\' => \'945\',\n  \'title\' => \'王建华\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/363f4c1b4db09147.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，内科副主任、消化内科主任医师，本科学历，学士学位。江苏省医学会消化内镜学分会ERCP学组委员、盐城市中西医结合学会脾胃学组副主任委员、盐城市医学会内科分会委员、盐城市医学会消化病学分会委员。擅长消化内科各种疑难危重病和诊治，熟练掌握胃镜、肠镜诊断及治疗技术，熟练开展ERCP、EUS及ESD技术，擅长于重症胰腺炎及炎症性肠病的治疗。近年来，在省级以上刊物发表论文10余篇；荣获盐城市科技进步二等奖..\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，内科副主任、消化内科主任医师，本科学历，学士学位。江苏省医学会消化内镜学分会ERCP学组委员、盐城市中西医结合学会脾胃学组副主任委员、盐城市医学会内科分会委员、盐城市医学会消化病学分会委员。擅长消化内科各种疑难危重病和诊治，熟练掌握胃镜、肠镜诊断及治疗技术，熟练开展ERCP、EUS及ESD技术，擅长于重症胰腺炎及炎症性肠病的治疗。近年来，在省级以上刊物发表论文10余篇；荣获盐城市科技进步二等奖3项、盐城市科技进步三等奖1项。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'急性重症胰腺炎、ERCP及其相关手术、EMR和ESD、胃肠道息肉切除术、胃肠道支架置入术等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'