INSERT INTO `p8_admin_log` VALUES ('1618','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=945&model=article\',\n  \'cid\' => \'945\',\n  \'title\' => \'吴旭东\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/bb5184f591ac878a.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\r\n男，消化内科副主任、主任医师，博士研究生学历。江苏省医学会消化病学分会委员、江苏省医学会消化内镜学分会早癌学组委员、江苏省医学会消化内镜学分会炎症性肠病学组委员。熟练开展内镜下各种诊断性及治疗性操作，擅长肝硬化等慢性肝病、重型胰腺炎及消化道出血的救治，对疑难危重消化系统疾病的诊治具有丰富的临床经验。近年来，在SCI期刊杂志发表论文7篇，编写专著2部；荣获江苏省新技术引进二等奖1项，盐城市..\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，消化内科副主任、主任医师，博士研究生学历。江苏省医学会消化病学分会委员、江苏省医学会消化内镜学分会早癌学组委员、江苏省医学会消化内镜学分会炎症性肠病学组委员。熟练开展内镜下各种诊断性及治疗性操作，擅长肝硬化等慢性肝病、重型胰腺炎及消化道出血的救治，对疑难危重消化系统疾病的诊治具有丰富的临床经验。近年来，在SCI期刊杂志发表论文7篇，编写专著2部；荣获江苏省新技术引进二等奖1项，盐城市新技术引进一等奖、二等奖各1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'慢性肝病、消化道肿瘤、消化系统功能性疾病等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add