INSERT INTO `p8_admin_log` VALUES ('1634','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article&cid=948&type=2\',\n  \'cid\' => \'948\',\n  \'title\' => \'刘成元 \',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_10/bf9121865ebbd27c.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，硕士，副主任医师。毕业于徐州医科大学研究生院，医学硕士，曾在上海瑞金医院感染科进修学习。江苏省医学会临床流行病学分会委员，盐城医学会感染病学会委员。临床经验丰富，擅长各种肝病，如慢性乙型肝炎、慢性丙型肝炎、肝硬化、酒精性肝病、自身免疫性肝病诊断和治疗，对不明原因发热，不明原因肝损，败血症，重症肾综合征出血热，重型肝炎及其并发症的救治均有丰富的临床经验。发表专业论文多篇，曾多次获...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，硕士，副主任医师。毕业于徐州医科大学研究生院，医学硕士，曾在上海瑞金医院感染科进修学习。江苏省医学会临床流行病学分会委员，盐城医学会感染病学会委员。临床经验丰富，擅长各种肝病，如慢性乙型肝炎、慢性丙型肝炎、肝硬化、酒精性肝病、自身免疫性肝病诊断和治疗，对不明原因发热，不明原因肝损，败血症，重症肾综合征出血热，重型肝炎及其并发症的救治均有丰富的临床经验。发表专业论文多篇，曾多次获得盐城市科技进步奖、新技术引进奖，参与多项研究课题。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'各种肝病，如慢性乙型肝炎、慢性丙型肝炎、肝硬化、酒精性肝病、自身免疫性肝病诊断和治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credi