INSERT INTO `p8_admin_log` VALUES ('1674','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=954&model=article\',\n  \'cid\' => \'954\',\n  \'title\' => \'蔡东旭\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_11/e76665ff4cd1aecd.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，康复医学科副主任，主任医师，本科学历。任盐城市医学会物理医学与康复医学副主任委员、盐城卫生技术学院康复治疗技术专业指导委员会委员。在省级以上刊物发表论文10余篇，获市政府科技进步二等奖1次，市政府自然科学优秀论文成果奖1项。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，康复医学科副主任，主任医师，本科学历。任盐城市医学会物理医学与康复医学副主任委员、盐城卫生技术学院康复治疗技术专业指导委员会委员。在省级以上刊物发表论文10余篇，获市政府科技进步二等奖1次，市政府自然科学优秀论文成果奖1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'擅长治疗颈椎病，腰椎间盘突出症及脑卒中康复\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'25ee35615f806ceb\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626233773');
INSERT INTO `p8_admin_log` VALUES ('1675','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=954&model=article\',\n  \'cid\' => \'954\',\n  \'title\' => \'李加斌\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任中医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_11/44b6e3259e1c96e5.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，康复医学科主任、主任中医师，门诊党支部书记，临床医学、中医学双本科学历。中国康复医学会创伤专业委员会常委、省中医药学会推拿分会常委、省康复医学会理事、省医学会物理医学与康复医学分会委员、省康复医学会脑损伤康复专业委员会常委、省康复医学会中医康复专业委员会常委、省中西医结合学会疼痛分会常委、市医学会物理医学与康复医学分会主任委员、市残疾人肢体残疾康复技术指导组组长。具体深厚的中西...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，康复医学科主任、主任中医师，门诊党支部书记，临床医学、中医学双本科学历。中国康复医学会创伤专业委员会常委、省中医药学会推拿分会常委、省康复医学会理事、省医学会物理医学与康复医学分会委员、省康复医学会脑损伤康复专业委员会常委、省康复医学会中医康复专业委员会常委、省中西医结合学会疼痛分会常委、市医学会物理医学与康复医学分会主任委员、市残疾人肢体残疾康复技术指导组组长。具体深厚的中西医理论功底和现代康复知识，能够娴熟地掌握中医推拿手法和现代康复治疗技术，擅长治疗颈、肩、腰腿痛，脑卒中，脊髓损伤，骨关节炎等病症，尤其对慢性复合性疼痛疾病有独到的见解和治疗方法。近年来，以第一作者在国家级、省级杂志上发表论文15篇，作为副主编出版医学专著3部，获盐城市科技奖二等奖2项、三等奖1项，获市政府自然科学优秀论文成果一等奖、二等奖各1项，获市新技术引进奖一等奖2项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'治疗颈、肩、腰腿痛、脑卒中、脊髓损伤、关节炎和部分妇科、儿科等疾病，尤其对慢性复合性疼痛疾病有独创的见解和治疗方法\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'