INSERT INTO `p8_admin_log` VALUES ('1693','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=956&model=article\',\n  \'cid\' => \'956\',\n  \'title\' => \'宋长志\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_14/1f08872e49b42747.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，博士,主任医师，硕士研究生导师，江苏省“333”工程高级人才，盐城市医学重点人才。2013年通过省卫生厅海外交流项目的考试，公派加拿大麦吉尔大学附属蒙特利尔总医院研修。主要从事关节、关节镜技术的基础和临床研究。擅长肩、髋、膝关节置换，翻修；关节镜下肩袖损伤修复，肩峰下撞击症，Bankart损伤，膝关节前后交叉韧带损伤重建，半月板损伤缝合修补，髋、踝关节疾病的治疗。SCI论文1篇，中华级论文6篇，核...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，博士,主任医师，硕士研究生导师，江苏省&ldquo;333&rdquo;工程高级人才，盐城市医学重点人才。2013年通过省卫生厅海外交流项目的考试，公派加拿大麦吉尔大学附属蒙特利尔总医院研修。主要从事关节、关节镜技术的基础和临床研究。擅长肩、髋、膝关节置换，翻修；关节镜下肩袖损伤修复，肩峰下撞击症，Bankart损伤，膝关节前后交叉韧带损伤重建，半月板损伤缝合修补，髋、踝关节疾病的治疗。SCI论文1篇，中华级论文6篇，核心期刊论文13篇；主持卫生部科研1项，卫生厅科研1项，江苏省&ldquo;333&rdquo;工程科研1项，盐城市科研3项。获盐城市科技进步三等奖1项，盐城市自然科学优秀学术成果奖一、二等奖2项，盐城市医学新技术引进奖一等奖2项。率先开展膝部、肩部肿瘤人工关节置换、肩关节骨关节炎人工全肩关节置换、微创髋关节置换、关节镜下半月板损伤缝合修补、关节镜下肩袖损伤修复、关节镜下踝关节撞击症等技术，填补了院内、市内多个项目的空白。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'髋、膝、肩等关节人工关节置换及翻修；关节镜微创治疗等\',\n  \'model\' => \'article\',\n  \'v