INSERT INTO `p8_admin_log` VALUES ('1706','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=957&model=article\',\n  \'cid\' => \'957\',\n  \'title\' => \'许永华\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_14/2b5e6611212fc03f.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，普通外科副主任医师，医学博士。德国海德堡大学医院、耶拿大学医院访问学者。现担任江苏省医学会外科学分会胆道学组委员、江苏省免疫学会肝脏病学组委员、江苏省医师协会胰腺学组委员、江苏省肿瘤学会青年委员、江苏省抗癌协会青年委员、盐城市医学会外科学分会委员、盐城市医学会创伤医学分会常务委员、盐城市医学会外科学分会青年委员会副主任委员。对普外科各类疾病有较为全面的理解和掌握，擅长肝胆胰疑难...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，普通外科副主任医师，医学博士。德国海德堡大学医院、耶拿大学医院访问学者。现担任江苏省医学会外科学分会胆道学组委员、江苏省免疫学会肝脏病学组委员、江苏省医师协会胰腺学组委员、江苏省肿瘤学会青年委员、江苏省抗癌协会青年委员、盐城市医学会外科学分会委员、盐城市医学会创伤医学分会常务委员、盐城市医学会外科学分会青年委员会副主任委员。对普外科各类疾病有较为全面的理解和掌握，擅长肝胆胰疑难疾病的诊断和治疗，能熟练开展腹腔镜胆囊切除术，腹腔镜胆总管探查术，规则性肝切除术，肝门部胆管癌根治术，胰十二指肠切除术等普外科重点手术。在本地区率先开展射频消融术治疗各类肝脏肿瘤。入选江苏省&ldquo;333高层次人才培养工程&rdquo;、江苏省&ldquo;六大人才高峰&rdquo;培养计划，是盐城市医学青年人才、盐城市青年岗位能手，徐州医科大学和南通大学医学院讲师。主持省级课题2项，市级课题1项，发表专业论文20余篇，其中第一作者SCI论文8篇。多次参加国际及国内、省内学术会议，并应邀发表专题学术报告，参赛手术视频多次荣获优秀手术视频奖项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肝胆胰疑难疾病的诊断和治疗，能熟练开展腹腔镜胆囊切除术，腹腔镜