INSERT INTO `p8_admin_log` VALUES ('1716','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=957&model=article\',\n  \'cid\' => \'957\',\n  \'title\' => \'孙大林\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_14/8060ca14ca61deee.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，主任医师，本科学历，南通大学副教授。任中华医学会普外科分会委员、盐城市肝移植工程核心成员。从医二十多年，具有丰富的临床工作经验，深入研究解剖学，将正常解剖、变异解剖、病理解剖灵活应用于工作实践中。手术大胆细腻，成功完成数百例高难度的精品手术。手术谱涉及甲状腺、乳腺、肝脏、胆道、胰腺、脾脏、胃肠、腹膜后等全科领域，尤其在普外科重危疑难疾病诊治方面有独到建树。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，主任医师，本科学历，南通大学副教授。任中华医学会普外科分会委员、盐城市肝移植工程核心成员。从医二十多年，具有丰富的临床工作经验，深入研究解剖学，将正常解剖、变异解剖、病理解剖灵活应用于工作实践中。手术大胆细腻，成功完成数百例高难度的精品手术。手术谱涉及甲状腺、乳腺、肝脏、胆道、胰腺、脾脏、胃肠、腹膜后等全科领域，尤其在普外科重危疑难疾病诊治方面有独到建树。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'普外科腹腔镜手术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'688e7c5eabd958d4\',\n  \'verifi