INSERT INTO `p8_admin_log` VALUES ('1724','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=957&model=article\',\n  \'cid\' => \'957\',\n  \'title\' => \'周广军\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/48fe14e8f7a7e488.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，普外科副主任，外一支部书记，主任医师，博士学位，硕士研究生导师。任江苏省医学会胃肠外科学组委员、江苏省抗癌协会胃癌专业委员会委员、盐城市医学会外科学分会副主任委员兼秘书长、盐城市医学会普外科分会副主任委员兼秘书长、盐城市医学会肿瘤外科分会副主任委员、兼职副教授、江苏省“333”工程培养对象。从事普外科专业20余年，熟练掌握普通外科专业理论知识，对普外科各类疾病有丰富的诊治经验，能熟...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，普外科副主任，外一支部书记，主任医师，博士学位，硕士研究生导师。任江苏省医学会胃肠外科学组委员、江苏省抗癌协会胃癌专业委员会委员、盐城市医学会外科学分会副主任委员兼秘书长、盐城市医学会普外科分会副主任委员兼秘书长、盐城市医学会肿瘤外科分会副主任委员、兼职副教授、江苏省&ldquo;333&rdquo;工程培养对象。从事普外科专业20余年，熟练掌握普通外科专业理论知识，对普外科各类疾病有丰富的诊治经验，能熟练完成胃肠、乳腺、甲状腺等各类普外科手术及腹腔镜微创手术，研究方向涉及胃肠道肿瘤的早期诊治、基因治疗及光动力治疗，胆道损伤修复的实验研究等。在《中华胃肠外科杂志》等核心期刊发表论文20余篇。所主持的相关课题获盐城市科技进步一等奖、二等奖、三等奖各1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'胃肠道肿瘤的手术治疗及综合治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.1