INSERT INTO `p8_admin_log` VALUES ('1725','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=957&model=article\',\n  \'cid\' => \'957\',\n  \'title\' => \'穆向明\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/d12709aea56ca6f3.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，盐城市第一人民医院副院长、主任医师，本科学历，学士学位，硕士研究生导师。现任江苏省政协委员，盐城市人大常委；民建江苏省委常委，民建盐城市委主委；南京医科大学兼职教授，硕士研究生导师；江苏省医学会胃肠道学组委员血管分会委员，江苏省结直肠腔镜学组委员，盐城市医学会普外分会主任委员；盐城市普外科学科带头人；江苏省劳动模范，江苏省首届医师成就奖获得者。熟练掌握普通外科专业理论知识，对普...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，盐城市第一人民医院副院长、主任医师，本科学历，学士学位，硕士研究生导师。现任江苏省政协委员，盐城市人大常委；民建江苏省委常委，民建盐城市委主委；南京医科大学兼职教授，硕士研究生导师；江苏省医学会胃肠道学组委员血管分会委员，江苏省结直肠腔镜学组委员，盐城市医学会普外分会主任委员；盐城市普外科学科带头人；江苏省劳动模范，江苏省首届医师成就奖获得者。熟练掌握普通外科专业理论知识，对普外科各类疾病有丰富的诊治经验，能熟练完成肝胆、胃肠、乳腺、甲状腺等各类普外科手术及腹腔镜微创手术及脾亢、门脉高压症的分流手术，研究方向涉及胃肠道肿瘤的腔镜诊治、胰腺癌的基因治疗以及胆道损伤修复的实验研究等。在国内外发表科研论文二十余篇；所主持的相关课题获盐城市科技进步一等奖、二等奖、三等奖多次。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'胃肠道肿瘤门脉高压和复杂的普外科手术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'0eb23770bd47fdae\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626246902');
INSERT INTO `p8_admin_log` VALUES ('1726','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=957&model=article\',\n  \'cid\' => \'957\',\n  \'title\' => \'查文章\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/321f7cd8abb5ddbb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，盐城市第一人民医院党委书记。主任医师，教授，博士生导师。担任江苏省第九届、第十届政协委员；江苏省第十三届人大代表；中国研究型消化病专业委员会常委；江苏省医学会外科委员会委员；江苏省胃癌协会常委；江苏省医学会外科营养学组副组长；江苏省肝脏外科分会委员；江苏省第十届、十一届政协委员；盐城市医院协会会长；盐城市医师协会副会长；盐城市青年医师联合会主席；英国皇家外科协会会员。作为盐城市...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，盐城市第一人民医院党委书记。主任医师，教授，博士生导师。担任江苏省第九届、第十届政协委员；江苏省第十三届人大代表；中国研究型消化病专业委员会常委；江苏省医学会外科委员会委员；江苏省胃癌协会常委；江苏省医学会外科营养学组副组长；江苏省肝脏外科分会委员；江苏省第十届、十一届政协委员；盐城市医院协会会长；盐城市医师协会副会长；盐城市青年医师联合会主席；英国皇家外科协会会员。作为盐城市外科学术带头人，熟练掌握普通外科各类疾病的诊疗技术，在省、市外科领域中享有较高声誉。曾连续三期入选江苏省&ldquo;333工程&rdquo;第二层次培养对象并荣获江苏省&ldquo;135工程&rdquo;医学杰出人才。率先在全市开展移植手术、微创外科手术，填补多项市内空白。开展并推广无血肝切除技术、无血胰十二指肠切除技术、三镜微创技术等多项技术，以精湛的技术赢得了广大患者的赞誉。先后在国内各级刊物发表论文近40篇，其中SCI收录5篇，多次参加国内外重大学术会议，曾获江苏省卫生厅科技进步二等奖、江苏省科技进步四等奖、悉尼大学&ldquo;优秀海外医师&rdquo;、盐城市新技术引进奖一等奖、盐城市科技进步奖二等奖等多项荣誉。参与编写国家重点图书《外科手术学图谱》，主持编写《肿瘤学外科学》等多部学术专著。学术论文及其成果多次被《新华日报》《健康报》《中国卫生信息报》《盐阜大众报》等多家主流新闻媒体报道。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肝癌肝切除、复杂性胆道手术、胰腺癌根治术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'364a88509ddfe2c4\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626246954');
INSERT INTO `p8_admin_log` VALUES ('1727','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article&cid=958&type=2\',\n  \'cid\' => \'958\',\n  \'title\' => \' 许进\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/8ac408d8c5240ca7.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，神经外科副主任医师，硕士研究生。擅长动脉瘤、高血压脑出血、垂体瘤、听神经瘤的显微手术治疗，对中央颅底肿瘤硬膜外入路、三叉神经痛面肌痉挛微血管减压术、神经内镜手术有深入研究，并积极开展脊柱、介入、缺血性疾病血管搭桥、CEA等手术。近年来，在SCI、省级以上医学刊物上发表医学论文10余篇，获市科技进步一等奖2项、二等奖1项，淮海科技三等奖1项。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，神经外科副主任医师，硕士研究生。擅长动脉瘤、高血压脑出血、垂体瘤、听神经瘤的显微手术治疗，对中央颅底肿瘤硬膜外入路、三叉神经痛面肌痉挛微血管减压术、神经内镜手术有深入研究，并积极开展脊柱、介入、缺血性疾病血管搭桥、CEA等手术。近年来，在SCI、省级以上医学刊物上发表医学论文10余篇，获市科技进步一等奖2项、二等奖1项，淮海科技三等奖1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'动脉瘤、高血压脑出血、垂体瘤、听神经瘤的显微手术治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/category-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'aa83ef140a7b21a0