INSERT INTO `p8_admin_log` VALUES ('1728','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=958&model=article\',\n  \'cid\' => \'958\',\n  \'title\' => \'孟成杰\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/787e27080faa009f.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，神经外科副主任医师，医学博士。师从国内知名脑血管病专家王中教授，并于北京天坛医院神经外科及北京神经外科研究所（世界三大神经外科中心之一）研修。擅长神经内镜经鼻垂体瘤切除术、显微镜下常见脑肿瘤切除术、颅内动脉瘤夹闭术、重型颅脑外伤手术、危重高血压脑出血手术治疗等。主持盐城市科技项目1项，参与国家自然科学基金项目研究，获盐城市2013年自然科学优秀学术成果奖。发表核心论文十余篇，其中SCI...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，神经外科副主任医师，医学博士。师从国内知名脑血管病专家王中教授，并于北京天坛医院神经外科及北京神经外科研究所（世界三大神经外科中心之一）研修。擅长神经内镜经鼻垂体瘤切除术、显微镜下常见脑肿瘤切除术、颅内动脉瘤夹闭术、重型颅脑外伤手术、危重高血压脑出血手术治疗等。主持盐城市科技项目1项，参与国家自然科学基金项目研究，获盐城市2013年自然科学优秀学术成果奖。发表核心论文十余篇，其中SCI 6篇，中华系列3篇。获得国家医学实用专利1项。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'神经内镜经鼻垂体瘤切除术、显微镜下常见脑肿瘤切除术、颅内动脉瘤夹闭术、重型颅脑外伤手术、危重高血压脑出血手术治疗等。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',