INSERT INTO `p8_admin_log` VALUES ('1729','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=958&model=article\',\n  \'cid\' => \'958\',\n  \'title\' => \'秦立森\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/68a16add976f57d3.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，神经外科副主任医师，医学博士。从事神经外科专业十五年余，现致力于神经外科内镜微创技术，擅长微创内镜血肿清除术，内镜经鼻蝶窦垂体瘤切除术，全内镜下三叉神经痛，面肌痉挛微血管减压术。发表SCI论文2篇，在《中华神经外科杂志》、《临床神经外科杂志》等核心期刊发表文章3篇，获得市科技进步二等奖1项。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，神经外科副主任医师，医学博士。从事神经外科专业十五年余，现致力于神经外科内镜微创技术，擅长微创内镜血肿清除术，内镜经鼻蝶窦垂体瘤切除术，全内镜下三叉神经痛，面肌痉挛微血管减压术。发表SCI论文2篇，在《中华神经外科杂志》、《临床神经外科杂志》等核心期刊发表文章3篇，获得市科技进步二等奖1项。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'微创内镜血肿清除术，内镜经鼻蝶窦垂体瘤切除术，全内镜下三叉神经痛，面肌痉挛微血管减压术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e2db4e314a3e9170\',\n  \'verifier\' => \'admin5\',\n  \'attri