INSERT INTO `p8_admin_log` VALUES ('1730','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=958&model=article\',\n  \'cid\' => \'958\',\n  \'title\' => \'贾锋\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_15/e4ebad25c81f1cb5.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，副主任医师，医学博士，中共党员，兼职副教授。江苏省333人才，盐城市青年人才。曾在上海华山医院，江苏省人民医院进修学习。江苏省脑外伤重症学组组员。擅长各种颅脑外伤，颅内感染的诊断，治疗，手术。擅长凸面脑膜瘤，胶质瘤，脊柱肿瘤的手术治疗。擅长各种前循环动脉瘤的手术治疗。对各种颅底肿瘤，鞍区肿瘤，颅脑深部肿瘤有丰富的治疗经验。发表SCI文章数篇，在国内核心杂志发表论文数篇。获得盐城市科技...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，副主任医师，医学博士，中共党员，兼职副教授。江苏省333人才，盐城市青年人才。曾在上海华山医院，江苏省人民医院进修学习。江苏省脑外伤重症学组组员。擅长各种颅脑外伤，颅内感染的诊断，治疗，手术。擅长凸面脑膜瘤，胶质瘤，脊柱肿瘤的手术治疗。擅长各种前循环动脉瘤的手术治疗。对各种颅底肿瘤，鞍区肿瘤，颅脑深部肿瘤有丰富的治疗经验。发表SCI文章数篇，在国内核心杂志发表论文数篇。获得盐城市科技进步奖，盐城市优秀论文奖。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'各种颅脑外伤，颅内感染的诊断，治疗，手术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \