INSERT INTO `p8_admin_log` VALUES ('1765','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=961&model=article\',\n  \'cid\' => \'961\',\n  \'title\' => \'刘国锋\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/9074644e4b2c996b.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，主任医师，本科学历。任中华医学会江苏分会胸心外科专科学会委员、中华医学会江苏分会肿瘤学分会常务委员、中华医学会盐城分会胸心外科学主任委员、中华医学会盐城分会肿瘤学主任委员。从事本专业临床一线工作30年，积累了丰富的经验，对普胸科的肺部、食管、贲门、纵膈手术操作均能得心应手，平均年主刀手术治疗普胸病人500例，治疗效果良好。在心脏外科方面治疗先天性心脏病，心脏瓣膜疾病约200余例，疗效优...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，主任医师，本科学历。任中华医学会江苏分会胸心外科专科学会委员、中华医学会江苏分会肿瘤学分会常务委员、中华医学会盐城分会胸心外科学主任委员、中华医学会盐城分会肿瘤学主任委员。从事本专业临床一线工作30年，积累了丰富的经验，对普胸科的肺部、食管、贲门、纵膈手术操作均能得心应手，平均年主刀手术治疗普胸病人500例，治疗效果良好。在心脏外科方面治疗先天性心脏病，心脏瓣膜疾病约200余例，疗效优良，在本市心胸外科起到带头作用。2004年成功实施1例心脏移植手术，2006年开始开展冠脉搭桥手术，2008年2例胸主动脉瘤手术。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'熟练并常规开展心胸外科各类手术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n