INSERT INTO `p8_admin_log` VALUES ('1768','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=962&model=article\',\n  \'cid\' => \'962\',\n  \'title\' => \'陈兰平\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/6c69c88357fd9e45.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，重症医学科主任医师，硕士研究生。现任盐城市重症医学、急救医学副主任委员及秘书长，盐城市危重孕产妇抢救专家以及省市突发应急救援专家组成员。2002年赴东南大学附属中大医院学习进修一年余，有着丰富的急危重疾病的基础理论知识、临床诊治经验。能够熟练进行休克、昏迷、抽搐、呼吸困难、咯血、消化道大出血等各种急症的诊断和处理，掌握内科各种危象、脑血管意外、多器官功能障碍综合征、多发性创伤、中毒...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>女，重症医学科主任医师，硕士研究生。现任盐城市重症医学、急救医学副主任委员及秘书长，盐城市危重孕产妇抢救专家以及省市突发应急救援专家组成员。2002年赴东南大学附属中大医院学习进修一年余，有着丰富的急危重疾病的基础理论知识、临床诊治经验。能够熟练进行休克、昏迷、抽搐、呼吸困难、咯血、消化道大出血等各种急症的诊断和处理，掌握内科各种危象、脑血管意外、多器官功能障碍综合征、多发性创伤、中毒等的诊断及抢救治疗，熟练进行CRRT、主动脉球囊反搏、气管切开、呼吸机应用、纤维支气管镜治疗、俯卧位通气、人工肝、血液灌流等技术，多次完成了各种重大车祸、突发公共卫生事件、中毒等的抢救任务。承担徐州医科大学、盐城卫生技术学院临床带教任务，多次被评为优秀带教老师。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'急危重症患者的抢救与治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.