INSERT INTO `p8_admin_log` VALUES ('1777','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=963&model=article\',\n  \'cid\' => \'963\',\n  \'title\' => \'李海如\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/32bbfa41c38e8580.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，市一院五官科医院副院长，口腔科主任，本科学历。任江苏省口腔学会理事、江苏省口腔学会口腔颌面外科专业委员会常务委员、江苏省医学会医学美学和美容分会委员、江苏省管理协会口腔分会委员、江苏省中西医口腔协会常务委员、江苏省医师学会口腔医师分会委员、江苏省医学会理事、口腔分会副主任委员、盐城市口腔学科带头人、盐城市“111”工程人才和“135”医学重点人才、南通大学兼职教授、中华慈善总会“国际...\r\n\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，市一院五官科医院副院长，口腔科主任，本科学历。任江苏省口腔学会理事、江苏省口腔学会口腔颌面外科专业委员会常务委员、江苏省医学会医学美学和美容分会委员、江苏省管理协会口腔分会委员、江苏省中西医口腔协会常务委员、江苏省医师学会口腔医师分会委员、江苏省医学会理事、口腔分会副主任委员、盐城市口腔学科带头人、盐城市&ldquo;111&rdquo;工程人才和&ldquo;135&rdquo;医学重点人才、南通大学兼职教授、中华慈善总会&ldquo;国际微笑列车&rdquo;唇腭裂矫治项目盐城地区负责人、江苏省&ldquo;333&rdquo;高层次人才培养工程首批中青年科学技术带头人，享受盐城政府特殊津贴专家，盐城市优秀科技工作者。重点研究口腔颌面部恶性肿瘤的诊治及其预后判断，颌骨切除重建修复术，各种复杂上下颌骨骨折的治疗及各种微小钛板固定，各种复杂单双侧唇腭裂整复，牙周病黏膜病的基础研究在核心刊物上发表论文50余篇，其中中华系列杂志发表论文6篇。主持承担市级科研项目6项，参与市级科研项目3项，获盐城市科技进步一等奖1项，二等奖3项，三等奖4项，获盐城市医学新技术引进一等奖1项，二等奖1项。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \